properties.query:
//  a per SAP note https://launchpad.support.sap.com/#/notes/3089413
// Systems without this parameter, or with values != "no" are vulnerable
// both (default) - old method allowed as client and server
// client - old method allowed as client
// server - old method allowed as server
// no - old method not allowed
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let TimeAgo= 7d;
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey, SystemRole, SystemUsage
| extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown' );
let Vulnerability= 'SAP - [CVE-2023-0014] Capture-replay vulnerability in a ';
let Remediation= ' SAP NetWeaver ABAP Server and ABAP Platform creates information about system identity in an ambiguous format. <br/> This may be exploited by malicious users to obtain illegitimate access to the system. <br/> Read SAP note 3281854 - FAQ for Security Note 3089413 for more details.';
let SafeValues= dynamic(['no']);
let MitigatedValues= dynamic(['client']);
let ConfiguredSystems= SAP_PAHI
| where TimeGenerated > ago(TimeAgo)
| where PARNAME == 'rfc/allowoldticket4tt'
| where PARSTATE == "A"
| summarize arg_max(PARDATE, PARVALUE, TimeGenerated, SYSTEMID) by SystemID, Host= HOSTNAME, SYSTEMID
| project-rename SystemNumber= SYSTEMID
| extend LastChanged= todatetime(PARDATE);
// ConfiguredSystems
let PotentialSystems= SAPAuditLog
| where TimeGenerated > ago(TimeAgo)
| project SystemID, Host, SystemNumber
| summarize by SystemID, Host, SystemNumber
| join kind= inner SelectedSystems on SystemID;
PotentialSystems | join kind= inner ConfiguredSystems on SystemID, Host, SystemNumber
| summarize SystemRole=any(SystemRole), Hosts= makeset(Host), SystemNumbers= makeset(SystemNumber) by SystemID, PARVALUE
| extend BagODetails= case(
(PARVALUE in (MitigatedValues)),bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' system- mitigated' ),'AlertDescription', Remediation, 'IsVulnerable', true, 'Severity', 'Medium' ),
(PARVALUE in (SafeValues)),bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' - system resolved' ),'AlertDescription', Remediation, 'IsVulnerable', false, 'Severity', 'Iformational' ),
//  else:
bag_pack('AlertName', strcat(Vulnerability, SystemRole, ' system'),'AlertDescription', Remediation, 'IsVulnerable', true, 'Severity', 'High' ))
| evaluate bag_unpack(BagODetails)
| project Hosts, SystemNumbers, PARVALUE, AlertName= columnifexists('AlertName', Vulnerability), AlertDescription= columnifexists('AlertDescription', Remediation), SystemID, Severity= columnifexists('Severity', '')
| extend ExtendedLink= 'https://www.cve.org/CVERecord?id=CVE-2023-0014', Dummy= ''
| extend AlertRuleUniqueName = 'capture-replayvulnerabilityinsapnetweaver[cve-2023-0014]'

// SAP - Medium - Critical authorizations assignment - New Authorization Value
// time span for audit events
let TimeAgo= 6h;
// New Assigned Objects
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUR','AUT']); // Authorization/Authorization Profile &B created / changed.
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
let ChangeDocs = SAPChangeDocsLog;
let UsersEmails = SAPUsersHeader()| summarize by User, Email, SystemID, Client | project-rename ClientID = Client;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | extend TimeGenRoleAuth = TimeGenerated;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let usersinRole =
    ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | summarize by SystemID, Role, UserAssigned;
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario = ComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario = SimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup  kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntities =
    SAPAuditLog
    | where MessageID in (AuditClasses)
    | where TimeGenerated > ago(TimeAgo)
    | summarize by TimeGenerated, ClientID, TerminalIPv6, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntities) on User
| where abs(datetime_diff('second', TimeGenRoleAuth, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| lookup UsersEmails on SystemID, ClientID, User
| project
TimeGenRoleAuth, SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host, UserAssignedEmail=Email1
| extend AlertRuleUniqueName = 'criticalauthorizationsassignment-newauthorizationvalue'

// sensitive data was released using transport system
// here you can exclude system users which are OK to export sensitive data using transport system
// by adding those users in the SAP_User_Config watchlist with a tag of 'ExportSensitiveDataOK'
// can also specify SAP Roles or SAP Profiles to exclude
let excludeUsersTags= dynamic(['ExportSensitiveDataOK']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by TableName= Table | extend IsSensitive= true;
let TransportReleaseTCODEs= dynamic(["SE10", "SE01"]);
let TransportReleasePrograms= dynamic(["RDDM0001"]);
let SensitiveDataExportViaCR= SAPCRLog
| where SystemID in (SelectedSystems)
| where isnotempty( SystemID)
| where isnotempty(TableName)
| lookup SensitiveTables on TableName
| where Status == "R" // released
| where Request startswith_cs SystemID
| project-rename ExportDT= UpdatedOn
| summarize Requests= make_set(Request,10), ExportDT= min(ExportDT) by Owner, RequestDescription= Description, TableName, SystemID, Instance, Host, IsSensitive;
let TransportReleaseTransactions= SAPAuditLog
| where  MessageID == "AU3"
| where SystemID in (SelectedSystems)
| where TransactionCode in(TransportReleaseTCODEs) or ABAPProgramName in (TransportReleasePrograms)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project-rename TransactionStartDT= UpdatedOn_t
| summarize TransactionCodes= make_set(TransactionCode, 3), ABAPProgramNames= make_set(ABAPProgramName, 3), Email= any(Email), TransactionStartDT= min(TransactionStartDT) by User, TerminalIPv6, Host, SystemID, ClientID, Instance;
TransportReleaseTransactions
| join kind= inner SensitiveDataExportViaCR on SystemID
| extend TimePassed= datetime_diff( "minute", ExportDT, TransactionStartDT)
| where TimePassed between (0 .. 60)
| extend AlertDescription= strcat("User has exported ",iff(IsSensitive, "sensitive", "tabular") ," data from a productive system using a transport")
| extend ExtendedDetails= pack("TransactionStartDT" , TransactionStartDT, "Owner", Owner, "RequestDescription", RequestDescription, 'TransactionCodes', TransactionCodes, 'ABAPProgramNames', ABAPProgramNames)
| extend Severity= iff(IsSensitive, "High", "Medium"), Dummy= ''
| project ExportedOn= ExportDT, User, TerminalIPv6, Host, SystemID, TransactionCode= TransactionCodes[0], ABAPProgramName= ABAPProgramNames[0], Email, ExtendedDetails, Requests, AlertDescription, TableName, IsSensitive, Severity, ClientID, Instance, Dummy
| extend AlertRuleUniqueName = 'dataexportedfromaproductionsystemusingatransport'

// SAP - (Preview) Dynamic Anomaly based Audit Log Monitor Alerts
let SelectedSystems =  dynamic(["All Systems"]);//can also do// let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do//let SelectedSeverities =  dynamic(["All Severities"])
// let SelectedRuleTypes = dynamic(["All RuleTypes"]);
let SelectedRuleTypes= dynamic(["AnomaliesOnly"]);
let SAPAuditAnomal = SAPAuditLogAnomalies(LearningTime = 14d, DetectingTime=1h, SelectedSystems= SelectedSystems,
SelectedSystemRoles= SelectedSystemRoles, SelectedSeverities= SelectedSeverities, SelectedRuleTypes= SelectedRuleTypes);
// Query
// get the anomalies
SAPAuditAnomal
// get the user privleged status
| lookup (SAPUsersGetPrivileged() | extend IsPrivilegedUser = true) on
$left.SystemID == $right.SystemID
,$left.User == $right.User
,$left.ClientID == $right.Client
| extend DetailedDescription = iff(IsPrivilegedUser, strcat('User ', User,  ' is a privileged user! <br/>', DetailedDescription), DetailedDescription)
| extend TimeGenerated = MaxTime
// handle threshold per system (configurable via the SAPAuditLogConfigurator watchlist)
//  apply the threshold criteria, consider that the watchlist describes hourly thresholds
| extend MinutesSpan = datetime_diff('Minute', MaxTime, MinTime )
| where EventCount > (Threshold * max_of(MinutesSpan, 10) / 60)
| extend DetailedDescription = iff(EventCount > 1, strcat(' Event occured ', EventCount, ' times within ', MinutesSpan + 1, ' minutes.<br/> ', 'The predefined threshold of ', Threshold,  ' Events per hour was exceeded! <br/>', DetailedDescription), DetailedDescription)
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| project-keep SystemID, Instance, MessageText, MessageClass, MessageID, ClientID, User, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, Host, CategoryName, DestinationEmail, DetailedDescription, Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, IsPrivilegedUser, EventCount, Threshold, TimeGenerated
| extend Source= 'SAPAuditAnom1 '
| extend AlertRuleUniqueName = 'dynamicanomalybasedauditlogmonitoralerts'

let TimeAgo = 65m;
let ioc_lookBack = 14d;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
let MaliciousIPs= SAPThreatIntelligenceIndicator
| where TimeGenerated >= ago(ioc_lookBack) and ExpirationDateTime > now()
| summarize LatestIndicatorTime = arg_max(TimeGenerated, *) by IndicatorId
| summarize ConfidenceScore= any(ConfidenceScore) by NetworkIP, EmailSourceIpAddress, NetworkDestinationIP, NetworkSourceIP, Active, Description, ThreatType
| where Active == true
// Picking up only IOC's that contain the entities we want
| where isnotempty(NetworkIP)
    or isnotempty(EmailSourceIpAddress)
    or isnotempty(NetworkDestinationIP)
    or isnotempty(NetworkSourceIP)
// As there is potentially more than 1 indicator type for matching IP, taking NetworkIP first, then others if that is empty.
// Taking the first non-empty value based on potential IOC match availability
| extend TI_ipEntity = iff(isnotempty(NetworkIP), NetworkIP, NetworkDestinationIP)
| extend TI_ipEntity = iff(isempty(TI_ipEntity) and isnotempty(NetworkSourceIP), NetworkSourceIP, TI_ipEntity)
| extend TI_ipEntity = iff(isempty(TI_ipEntity) and isnotempty(EmailSourceIpAddress), EmailSourceIpAddress, TI_ipEntity)
//Exclude local addresses, using the ipv4_is_private operator
| where TI_ipEntity !startswith "fe80"
    and TI_ipEntity !startswith "::"
    and TI_ipEntity !startswith "127."
| summarize by TI_ipEntity, TIDescription= Description, ThreatType, ConfidenceScore
| extend IsPrivate= ipv4_is_private(TI_ipEntity);
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | join kind=inner (MaliciousIPs) on $left.TerminalIPv6 == $right.TI_ipEntity
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize ConfidenceScore= any(ConfidenceScore), IsPrivate=any(IsPrivate), TIDescription=any(TIDescription), ThreatType= any(ThreatType), DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
    FileDownloads
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes data from a malicious ", iff(IsPrivate, "private","public")," IP address ")
    | extend AlertDetails= strcat("Threat Type: ", ThreatType, ", Threat Description: ", TIDescription), Dummy= ''
| extend AlertRuleUniqueName = 'filedownloadedfromamaliciousipaddress'

// SAP - High volume of potentially sensitive data exported
// high volume of file downloads by users who were obsereved accessing sensitive data
// or sensitive programs/ transactions
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 60m;
let ByteCountThreshold= 800000; //  Threshold in Bytes for entire lookback period
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54","SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions') | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram:string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports| summarize by ABAPProgram;
// CONFIGURATION OPTION- exclude system users which are OK to download massive amounts of sensitive data
// by adding those users in the SAP_User_Config watchlist with a tag of 'MassiveDownloadsOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['MassiveDownloadsOK','SAP_ROLE1', 'SAP_PROFILE1']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9" or MessageID == "CUZ"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3) by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , SourceAction= strcat("after executing sensitive program ", ABAPProgramName), TransactionCode, TableOrProgram= ABAPProgramName ;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3" or MessageID == "AU4"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes,TableAccessTcodes)) or Variable1 in (set_difference(SensitiveTcodes,TableAccessTcodes))
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, SourceAction= strcat("after executing sensitive transaction ", TransactionCode), TransactionCode, TableOrProgram= TransactionCode;
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | where TransactionCode in (SensitiveTcodes) or ABAPProgramName in (UnionABAP)
    | join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude // exclude specific users
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by TimeDownloaded= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
// Time window join to find dowloads after table access
FileDownloads | join kind=inner (SensitiveTableAccess | union SensitiveProgramExec, SensitiveTcodeExec) on User, SystemID
    | where (TimeAccessed- TimeDownloaded) between (-(TimeBetweenAccessAndDownload*2) .. 0h)
    | summarize Activities= tostring( make_set_if(Activities, tostring(Activities) != "")), DownloadsByUser= max(DownloadsByUser), ByteCount=max(ByteCount), TablesOrPrograms=make_set(TableOrProgram,10), SourceActions= make_set(SourceAction,10), TimeAccessed= any(TimeAccessed) by TimeDownloaded, User, SystemID, ClientID, Email, Host , TerminalIPv6, Paths= tostring(Paths), Instance
    | where ByteCount > ByteCountThreshold
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes of potentially sensitive data ")
    | extend AlertDetails= strcat(Details , tostring(SourceActions)), Dummy= ''
| extend AlertRuleUniqueName = 'highvolumeofpotentiallysensitivedataexported'

// SAP - Potentially sensitive data exported via spool request
// Spool requests made by users who were obsereved accessing sensitive data
// or sensitive programs/ transactions
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 30m;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables')
    | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54", "SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions')
    | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram: string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports
    | summarize by ABAPProgram;
// CONFIGURATION OPTION- exclude system users which are OK to print sensitive data
// by adding those users in the SAP_User_Config watchlist with a tag of 'PrintSensitivesOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['PrintSensitivesOK', 'SAP_ROLE1', 'SAP_PROFILE1']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)
    | summarize by User2Exclude=SAPUser;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3)
        by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, Email, Host, TerminalIPv6
        , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table, Instance;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize
        by
        TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload),
        SystemID,
        ClientID,
        User,
        ABAPProgramName,
        Email,
        Host,
        TerminalIPv6,
        SourceAction= strcat("after executing sensitive program ", ABAPProgramName),
        TransactionCode,
        TableOrProgram= ABAPProgramName,
        Instance;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes, TableAccessTcodes))
    | summarize
        by
        TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload),
        SystemID,
        ClientID,
        User,
        ABAPProgramName,
        SourceAction= strcat("after executing sensitive transaction ", TransactionCode),
        TransactionCode,
        TableOrProgram= TransactionCode,
        Email,
        Host,
        TerminalIPv6,
        Instance;
// Get all spool requests
let SpoolRequests= SAPSpoolLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | summarize
        by
        SpoolRequestNumber,
        ClientID,
        User,
        RecipientofSpoolRequest,
        SpoolRequestSuffix2,
        SpoolRequestTitle,
        SystemID,
        OutputDevice,
        DeleteSpoolRequestAuto,
        TimeRequested= TimeGenerated,
        ValueAuthCheck;
let SpoolOutputs= SAPSpoolOutputLog
    | where TimeGenerated > ago (TimeAgo * 2) and ingestion_time() > ago(TimeAgo)
    | summarize
        by
        SpoolRequestNumber,
        User,
        RecipientofSpoolRequest,
        PhysicalFormatType,
        SystemID,
        ClientID,
        TimePrinted= TimeGenerated
    | lookup (SAP_USR02
        | summarize by SystemID, RecipientofSpoolRequest= BNAME, USTYP, ClientID= MANDT)
        on RecipientofSpoolRequest, SystemID, ClientID
    | extend
        HasPrinted= true,
        PrintRecievedByAnother= iff(User == RecipientofSpoolRequest, false, iff(isempty(USTYP), false, true));
SpoolRequests
| join kind=inner (SensitiveTableAccess
    | union SensitiveProgramExec, SensitiveTcodeExec)
    on User, SystemID
| where (TimeAccessed - TimeRequested) between (-(TimeBetweenAccessAndDownload * 2) .. 0h)
| lookup SpoolOutputs on SpoolRequestNumber, SystemID, ClientID
| summarize Activities= tostring(make_set_if(Activities, tostring(Activities) != ""))
    , TablesOrPrograms=make_set(TableOrProgram, 10), SourceActions= make_set(SourceAction, 10)
    , TimeAccessed= min(TimeAccessed), TimeRequested= min(TimeRequested), HasPrinted=max(HasPrinted)
    , PrintRecievedByAnother=max(PrintRecievedByAnother), RecipientofSpoolRequest= max(RecipientofSpoolRequest)
    by
    User,
    SystemID,
    Email,
    Host,
    TerminalIPv6,
    ClientID,
    Instance
| extend PackedDetails= pack_all()
| extend Details= strcat("User ", User, " has ", iff(HasPrinted, "printed", "requested printing of"), " potentially sensitive data ")
| extend AlertDetails= strcat(Details, tostring(SourceActions), iff(PrintRecievedByAnother, strcat("<br/> Print has been pulled by another User ", RecipientofSpoolRequest), "")), Dummy= ''
| extend AlertRuleUniqueName = 'printingofpotentiallysensitivedata'

// SAP - Sensitive data saved into a USB drive
// Data exported via files and saved into a recently mounted USB drive in proximity to an execution of a sensitive transaction, a sensitive program or direct access to sensitive table.
// !!!IMPORTANT!!!
// please keep "Lookup data from the last" to be 14 days to allow coverage of USB drive mount events
// actual lookback is set by setting TimeAgo
let TimeAgo= 4h;
let TimeBetweenAccessAndDownload= 60m;
let FilesDownloadedToUSB= DeviceFileEvents
    | where ingestion_time() >= ago(TimeAgo)
    | join kind=inner  (DeviceEvents
    | where ActionType == "UsbDriveMounted"
    | extend DriveLetter = tostring(AdditionalFields.DriveLetter)
    | summarize MountedDriveLetters = make_set(DriveLetter, 26) by DeviceId, DeviceName)
    on DeviceId
| extend TargetDriveLetter = tostring(split(FolderPath, "\\")[0])
| where set_has_element(MountedDriveLetters, TargetDriveLetter)
| project USBFileSize= FileSize, USBFolderPath= FolderPath;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles=  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
// let SelectedSystemRoles= dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// Get sensitive Tables
// CONFIGURATION OPTION- list sensitive tables in the 'SAP - Sensitive Tables' watchlist
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables') | summarize by Table;
let TableAccessTcodes= dynamic(["SE16", "SE16N", "SE11", "SE16H", "SM30", "SE12", "SM31", "SE16H", "SE14", "SE54","SE17", "SE16T", "DB01", "DB02"]);
// CONFIGURATION OPTION- list sensitive transactions in the 'SAP - Sensitive Transactions' watchlist
let SensitiveTransWL= toscalar(_GetWatchlist('SAP - Sensitive Transactions') | summarize TransactionCodes= make_set(TransactionCode));
// CONFIGURATION OPTION- exclude TCODEs which are not to be considered
let ExcludedTcodes= dynamic(["S000", ""]);
let SensitiveTcodes= (set_difference(set_union(SensitiveTransWL, TableAccessTcodes), ExcludedTcodes));
// CONFIGURATION OPTION- list sensitive programs in the 'SAP - Sensitive ABAP Programs' watchlist
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs');
let fixedABAPReports = datatable(ABAPProgram:string) ["RSPFLDOC"];
let UnionABAP = union isfuzzy=true SensitiveABAPReports, fixedABAPReports| summarize by ABAPProgram;
// Get direct sensitive table access
let SensitiveTableAccess= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "DU9"
    | where SystemID in (SelectedSystems)
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | summarize Activities= make_set(Activity, 3) by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , Table, SourceAction= strcat("after visiting transaction ", TransactionCode, " and viewing sensitive table ", Table), TransactionCode, TableOrProgram= Table;
// get sensitive program execution
let SensitiveProgramExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AUW"
    | where SystemID in (SelectedSystems)
    | where ABAPProgramName in (UnionABAP)
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName
    , SourceAction= strcat("after executing sensitive program ", ABAPProgramName), TransactionCode, TableOrProgram= ABAPProgramName ;
// get sensitive transaction execution
let SensitiveTcodeExec= SAPAuditLog
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where MessageID == "AU3"
    | where SystemID in (SelectedSystems)
    // make sure sensitive transactions do not contain the TableAccessTcodes which are checked for the specific table name
    | where TransactionCode in (set_difference(SensitiveTcodes,TableAccessTcodes))
    | summarize by TimeAccessed= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, ABAPProgramName, SourceAction= strcat("after executing sensitive transaction ", TransactionCode), TransactionCode, TableOrProgram= TransactionCode;
// Get all downloads
let FileDownloads= SAPAuditLog
    | where MessageID == "AUY"
    | where TimeGenerated > ago (TimeAgo*2) and ingestion_time() > ago(TimeAgo)
    | where SystemID in (SelectedSystems)
    | where TransactionCode in (SensitiveTcodes) or ABAPProgramName in (UnionABAP)
    | join kind= innerunique FilesDownloadedToUSB on $left.Variable3== $right.USBFolderPath
    | extend ByteCount= toint(replace_string(replace_string(Variable1, ".",""), ",","")), Code=Variable2, Path= Variable3
    | summarize DownloadsByUser = count(), Paths= make_set(Variable3, 10), ByteCount=sum(ByteCount) by TimeDownloaded= bin(TimeGenerated, TimeBetweenAccessAndDownload), SystemID, ClientID, User, TerminalIPv6, Email, Host, TransactionCode, Instance;
// Time window join to find dowloads after table access
FileDownloads | join kind=inner (SensitiveTableAccess | union SensitiveProgramExec, SensitiveTcodeExec) on User, SystemID
    | where (TimeAccessed- TimeDownloaded) between (-(TimeBetweenAccessAndDownload*2) .. 0h)
    | summarize Activities= tostring( make_set_if(Activities, tostring(Activities) != "")), DownloadsByUser= max(DownloadsByUser), ByteCount=max(ByteCount), TablesOrPrograms=make_set(TableOrProgram,10), SourceActions= make_set(SourceAction,10), TimeAccessed= any(TimeAccessed) by TimeDownloaded, User, SystemID, ClientID, Email, Host , TerminalIPv6, Paths= tostring(Paths), Instance
    | extend PackedDetails= pack_all()
    | extend Details= strcat("User ", User, " has downloaded ", ByteCount, " bytes of potentially sensitive data ")
    | extend AlertDetails= strcat(Details , tostring(SourceActions)), Dummy= ''
| extend AlertRuleUniqueName = 'sensitivedatasavedintoausbdrive'

// Activation or Deactivation of ICF Service
// Materialize the data for self join to be esier
let TimeAgo= 2h;
let ICFDataChanges = materialize(SAPTableDataLog
| where TimeGenerated > TimeAgo
| where TableName == "ICFSERVLOC"
| where NewValue == "X" or NewValue =="" or isempty( NewValue)// X - Status is activated, "" Not activated now
| where TableField == "ICF_NAME" or TableField == "ICFACTIVE"// Service name// Active status
| project OldValue, NewValue, LogKey, DBLogID, SystemID, TimeGenerated, UserName, Host, Instance);
let ActDeactICF =
ICFDataChanges
| where OldValue == NewValue and OldValue != ""
| lookup ICFDataChanges on
LogKey, DBLogID // Lookup for combining the data
| extend ICFServiceStatus = iff(NewValue1 == "X", "Activated", iff(NewValue1 == "" or isempty( NewValue1) and OldValue1 == "X" ,"Deactivated","No Activity"))
| extend Service = split(LogKey," ")[0];
ActDeactICF
| lookup SAPUsersHeader() on SystemID, $left.UserName== $right.User
| extend AlertText= strcat("Service ", Service, " ", ICFServiceStatus, " by User ", UserName, " in System ", SystemID)//{{Service}} {{ICFServiceStatus}} by User {{User}} in System {{SystemID}}
| project TimeGenerated, Service, PrimaryIP, PrimaryEmail, KnownIPs, KnownEmails, LastKnownIP, User= UserName, Host, SystemID, Client, Instance, ICFServiceStatus, AlertText
| extend Dummy = ''
| extend AlertRuleUniqueName = 'activationordeactivationoficfservice'

// Define Variables
// Audit Log Classes - Authorizations for user changed
// the actual lookback period is 1h. The alert looks back further to allow for user master data to be built slowly
let TimeAgo= 12h;
let Identity = 'IDENTITY';
let ProfileChangeDoc = 'SUSR_UST04';
let Insert = "I";
let logsThreshold = 3600; // 3600 seconds
let AuditClasses = dynamic(['AUB']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveProfiles =  _GetWatchlist('SAP - Sensitive Profiles');
let fixedProfile = datatable(Profile:string)['SAP_ALL','SAP_NEW'];// Maintain these if System doesn't have CR's
// here you can exclude system users which are OK to assign a Sensitive profile
// by adding those users in the SAP_User_Config watchlist with a tag of 'AssignSensitiveProfileOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['AssignSensitiveProfileOK']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string, Instance: string, ClientID: string, UpdatedOn_t: datetime)[];
let ChangeDocs =
(union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs)
| where ingestion_time() > ago(TimeAgo)
| project-rename ChangedOn= UpdatedOn_t;
let IdentityChangeDocuments =
// Identity Change documents which represents profiles assignment
    ChangeDocs
    | where ObjectClass == Identity // Identity
    and TableName == ProfileChangeDoc // Profile Change Doc
    and TypeofChange_Item == Insert // Insert
    | extend Profile = ChangedTableKey
    | extend UserAssigned = ObjectID;
let UnitedProfiles =
toscalar(union fixedProfile, SensitiveProfiles
| summarize Profiles = make_list(Profile));
// Query logic
SAPAuditLog
| where ingestion_time() > ago(TimeAgo)
| where MessageID in (AuditClasses)
| project-rename AuditedOn= UpdatedOn_t
| summarize by AuditedOn, TerminalIPv6, User, Host, Email
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| lookup kind = leftouter (IdentityChangeDocuments) on User
| where Profile in (UnitedProfiles)
| where abs(datetime_diff('second',ChangedOn,AuditedOn)) <= logsThreshold
or isnull(AuditedOn)
| project
// Details
ChangedOn, Instance , SystemID, ClientID, Profile, User,  UserAssigned,
Email, TerminalIPv6, Host, AuditedOn
| extend Dummy= '',  PackedDetails= pack_all()
| extend AlertRuleUniqueName = 'assignmentofasensitiveprofile'

 // Assignment of a sensitive role
 // !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
let TimeAgo= 2h;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["Production", "UAT"]); //can also do// let SelectedSystemRoles =  dynamic(["All System Roles"];
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage;
let Roles = 'PFCG';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let logsThreshold = 180; // max time in seconds between audit record and change record
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB', 'AUD']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveRoles =  _GetWatchlist('SAP - Sensitive Roles');
let fixedRoles = datatable(Role: string)['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
// here you can exclude system users which are OK to assign sensitive roles
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveRoleOK'
let excludeUsersTags= dynamic(['SensitiveRoleOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
let UnitedRoles = toscalar(union fixedRoles, SensitiveRoles
| summarize Roles = make_list(Role));
// get the assignments made
let ChangeCheck =SAPChangeDocsLog
| where TimeGenerated > ago(TimeAgo+ 1h)
| where UpdatedOn_t > ago(TimeAgo)
| join kind=inner SelectedSystems on SystemID
| where ObjectClass == Roles // Roles
    and TableName == UsersRoles // Users Roles
    and TypeofChange_Item == Insert // Insert
| extend IsSensitive= ObjectID in (UnitedRoles)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| extend UserAssigned = trim_end(@"[^\w]+" ,extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey))
| project-rename Role=ObjectID, ChangedOn= UpdatedOn_t;
// enrich with the host and IP address from the audit log
SAPAuditLog
| where TimeGenerated > ago(TimeAgo+ 1h)
| where UpdatedOn_t > ago(TimeAgo)
| join kind=inner SelectedSystems on SystemID
| where MessageID in (AuditClasses)
| summarize by UpdatedOn_t, TerminalIPv6, User, Host, Email, ClientID, SystemID
| extend User= trim_end(@"[^\w]+" , User)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| lookup kind = inner ChangeCheck on User, ClientID, SystemID
| extend TimeDiff= abs(datetime_diff('second', ChangedOn, UpdatedOn_t))
| where TimeDiff <= logsThreshold
// determine severity- self assignment is high, sensitive role is medium, not sensitive is low
| extend Severity= case(User == UserAssigned, "High", IsSensitive, "Medium","Low")
// remove this filter if you need to be informed on non-sensitive role assignment as well:
| where Severity != "Low"
| order by ChangeNumber, SystemID, ClientID, TimeDiff asc
| extend RowRank= row_number(0, prev(ChangeNumber) != ChangeNumber)
| where RowRank == 0 // only the closest audit record
| project ChangedOn, UpdatedOn_t, Instance , SystemID, ClientID, Role, User, UserAssigned, Email, TerminalIPv6, Host, Severity, IsSensitive, SystemRole
| extend PackedDetails= pack_all(), Dummy=' '
| extend AlertDescription= iff(User == UserAssigned,strcat('User ', User, ' has self-assigned a ', iff(IsSensitive,'sensitive',''),' role '),strcat('User ', User, ' has Assigned a ',  iff(IsSensitive,'sensitive',''),' role to User ', UserAssigned))
| extend AlertDescription= strcat(AlertDescription, " in a ", tolower(SystemRole), " system")
| lookup (SAPUsersEmail | project SystemID, ClientID, UserAssigned= User, UserAssignedEmail= Email) on UserAssigned, SystemID, ClientID
| extend Tactics= "Privilege Escalation"

// SAP - Brute Force (RFC)
// Define constants
let TimeAgo= 60m;//time to look for brute forcing
let LearningTime= 3d;// history to learn
let Threshold= 0.5; // 0.1 is sensitive, 2 is indifferent
let PerIPPerMinute = 60; // number of calls permitted per minute from a single source
// determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// here you can exclude system users which are OK to use RFC heavily
// by adding those users in the SAP_User_Config watchlist with a tag of 'MultipleRFCOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['MultipleRFCOK', 'SAP_ROLE', 'SAPPROFILE']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)
    | summarize by User2Exclude=SAPUser;
// COCan also exclude users by user classes
let UserClassesToExclude= dynamic(["Class2Exclude"]);
let UsersByClass= (SAP_USR02
| where CLASS in (UserClassesToExclude)
| summarize arg_max(TimeGenerated, *) by GLTGB, SystemID, User=BNAME, ClientID= MANDT
| summarize arg_max(GLTGB, CLASS) by SystemID, User, ClientID);
// Query logic
let RFCLogons= SAPAuditLog
| where MessageID == "AU6" // RFC Logon Failed
| where SystemID in (SelectedSystems)
| where TimeGenerated > ago (LearningTime)
| extend IngestionTime= ingestion_time()
| join kind= leftantisemi UsersByClass on User, SystemID, ClientID
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude;
// get current excessive callers
let PotentialExcessiveCallers= RFCLogons
| where TimeGenerated > ago(TimeAgo + 1h)
| where IngestionTime > ago(TimeAgo)
| extend IP= coalesce(TerminalIPv6, Computer, TERM_IPV6)
| summarize CallCount= count(), StartTime = min(UpdatedOn_t), EndTime = max(UpdatedOn_t) by IP
| where CallCount > PerIPPerMinute* TimeAgo/ (1m);
// build series of logons from these addresses
let RFCLogonHistory= RFCLogons
| project TerminalIPv6, Computer, TERM_IPV6, UpdatedOn_t
| extend IP= coalesce(TerminalIPv6, Computer, TERM_IPV6)
| where IP in ((PotentialExcessiveCallers | summarize IPs= makeset(IP,1000)))
| make-series CallCount = count() on UpdatedOn_t from ago(LearningTime) to now() step TimeAgo by IP;
// learn the history and perform judgement
let ExcessiveCallers= RFCLogonHistory
| extend (CallCountAnome, score, baseline) = series_decompose_anomalies(CallCount, Threshold= Threshold, Test_points= 1)
// | where CallCountAnome[-1] > 0 // only excessive
| summarize IPs= makeset(IP,1000);
// get the actual call from these IPs
RFCLogons
| where TimeGenerated > ago(TimeAgo + 1h)
| where IngestionTime > ago(TimeAgo)
| where TerminalIPv6 in ((ExcessiveCallers)) or Computer in ((ExcessiveCallers)) or TERM_IPV6 in((ExcessiveCallers))
| extend IP= coalesce(TerminalIPv6, Computer, TERM_IPV6)
| lookup PotentialExcessiveCallers on IP
| summarize LoginAttemptsMadebyIP= any(CallCount), CallCountPerUserSystem= count(), StartTime= any(StartTime), EndTime=any(EndTime) by IP, Email, User, SystemID, ClientID, Host
| extend ActivityDetailed= bag_pack("Email", Email, "User", User, "SystemID", SystemID, "ClientID", ClientID, "Host", Host, "CallCountPerUserSystem", CallCountPerUserSystem)
| summarize LoginAttemptsMadebyIP= any(LoginAttemptsMadebyIP), Activities= make_set(ActivityDetailed, 30), StartTime =any(StartTime), EndTime = any(EndTime)
, (CallCountPerUserSystem, User, Email, SystemID, ClientID, Host)= argmax(CallCountPerUserSystem, User, Email, SystemID, ClientID, Host) by IP
| lookup SelectedSystems on SystemID
| extend AlertName= strcat("SAP - Potential brute force attack (RFC) on a ", tolower(SystemRole), " system, ", LoginAttemptsMadebyIP, " calls within ", datetime_diff("minute", EndTime, StartTime), " minutes")
| extend AlertDescription= strcat("Source has produced ", LoginAttemptsMadebyIP, " calls within ", datetime_diff("minute", EndTime, StartTime)," minutes, most of which were made to system ", SystemID, ". The permitted limit of calls per minute is ", PerIPPerMinute , ". ")
| extend AlertRuleUniqueName = 'bruteforce(rfc)'

// Change in a Sensitive Privileged User
// time span for audit events
let AuditTimeAgo= 75m;
// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'BU2']);
// here you can exclude system users which are OK to modify other users' master data
// by adding those users in the SAP_User_Config watchlist with a tag of 'ChangeUserMasterDataOK'
let excludeUsersTags= dynamic(['ChangeUserMasterDataOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
let UsersEmails = SAPUsersHeader()
| project-keep User, SystemID, Client, Email
| project-rename ClientID= Client, ChangedEmail=Email, ChangedUser=User;
SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| where MessageID in (AuditClasses)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
// The user that we are making change in is a sensitive privileged user
| extend ChangedUser = Variable1
| join kind= inner (SAPUsersGetPrivileged())
on $left.ClientID == $right.Client
, $left.SystemID == $right.SystemID
, $left.ChangedUser == $right.User
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| extend MessageTextDyna = replace_string(MessageText, "User", "Privileged User")
| extend MessageTextDyna = replace_string(MessageTextDyna, "user", "Privileged User")
| lookup UsersEmails
on SystemID, ClientID, ChangedUser
| project-keep TimeGenerated, Instance,  SystemID, ClientID, User, MessageText, MessageID, Email,  TerminalIPv6, Host, ChangedUser, ChangedEmail, MessageTextDyna
| extend Email= iff(isempty(Email),User, Email), Host= iff(isempty(Host), TerminalIPv6, Host), Dummy= ''
| extend ChangedEmail= iff(isempty(ChangedEmail),ChangedUser, ChangedEmail)
| extend PackedDetails= pack_all()
| extend AlertRuleUniqueName = 'changeinasensitiveprivilegeduser'

// New Assigned Users
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB','AUD']); // Authorizations for user &A changed. User Master Record Changed
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
// Maintain these if System doesn't have CR's
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string)[];
let ChangeDocs =
union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | summarize by SystemID, Role, AuthObject, ObjField, ObjFieldValue;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let fixedComplexAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '*',
    'S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '02']; // Maintain these if WatchList is not available
let fixedSimpleAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_TCODE', 'TCD', '*', 'NOT_IN_USE', '',
    'S_TZONE', 'ACTVT', '*', 'NOT_IN_USE', '']; // Maintain these if WatchList is not available
let usersinRole =
    ChangeDocs
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | extend TimeGenUserinRole = TimeGenerated;
    //| summarize by TimeGenerated, SystemID, ClientID, Role, UserAssigned, User
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario =
    union ComplexAuth, fixedComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario =
    union SimpleAuth, fixedSimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntites =
    SAPAuditLog
    | where TimeGenerated >= alertSched
    | where MessageID in (AuditClasses)
    | summarize by TimeGenerated, TerminalIPv6, ClientID, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntites) on User
| where abs(datetime_diff('second', TimeGenUserinRole, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| project
    // Details
TimeGenUserinRole , SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'criticalauthorizationsassignment-newuserassignment'

// ***IMPORTANT***- Lookback period is maintained in the KQL
// please keep the Query scheduling parameter of "Lookup data from the last" to be 14 days so
// the algorithm can detect anomalies across history
let LogLookBack= 14d;
let StepSize= 1d;
// handle the TimeGenerated -> ingestion gap
let Tolerance= 15m;
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// 0.1 is very sensitive and is required for low activity workspaces with under 1M events a day.
// 0.5 can work better for high scale WSs (between 1M and 10M records a day). 1 can be used for WSs with over 10m records a day
let Threshold= 0.1;
let SystemLevelIssues= SAPConnectorHealth()
    | summarize arg_max(LastSeen, *) by SystemID // last status per SystemID
    | where SystemID !in (dynamic(["N/A", "AGE", "Agent Version Check"])) // agents checks are not interesting
    | where MessageCode != "OKOK" // only show problems
    | where Status == "Yellow" or Status == "Red"
    | extend Severity= case(Status == "Yellow", "Medium", Status == "Red", "High", "Unknown")
    | extend Log= 'Entire system'
    | extend Details= replace_string(Details, "System", strcat("SAP System ", SystemID));
let LogContinuationTS=  SAPConnectorOverview()
    | where TimeGenerated > ago(LogLookBack)
    | project Type, TimeGenerated, SystemID= SystemID_s
    | make-series RecordCount= count(), LastSeen= max(TimeGenerated) on TimeGenerated from ago(LogLookBack) to now() step StepSize by Log= Type, SystemID;
// LogContinuationTS
let LogContinuationAnomal= LogContinuationTS
    | extend (RecordCountAnomalies, score, baseline) = series_decompose_anomalies(RecordCount, Threshold= Threshold, Test_points= 1)
    | where array_index_of(RecordCountAnomalies, -1, -1) > 0 // some log is missing in the last bucket
    // to allow for some tolerance, check if last seen is in the previous bin
    | extend LastSeen= (max_of(todatetime(LastSeen[-1]),todatetime(LastSeen[-2])))
    | extend IsTolerable= now() < (LastSeen + Tolerance + 1d)
    | extend Severity= iff(RecordCount[-1] == 0 and not(IsTolerable), "Low", "Informational") // determine severity according to certainty
    | extend Details= strcat('SAP- Potential log loss for log ', Log, ' from System ', SystemID)
    | extend ExtendedDetails= strcat('Log ', Log, ' from System ', SystemID, ' has produced ', RecordCount[-1], ' recods since ', format_datetime(todatetime(TimeGenerated[-1]), 'yy-MM-dd [hh:mm:ss tt]'), '. Expected number of records is ', round(toint(baseline[-1]), 0));
(LogContinuationAnomal
| join kind=leftantisemi (SystemLevelIssues
    | where Status == "Red"
    | summarize by SystemID)
    on SystemID) // System-wide issues should overtake log level issues
| union SystemLevelIssues
| join kind= inner SelectedSystems on SystemID
| where Severity != "Informational" // optionally can have these cases produce alerts by deleting this filter
| where not (SystemRole == "Production" and Log startswith("ABAPCR")) // CR logs coming from production systems are unpredictable
| project Log, SystemID, SystemRole, LastSeen, Severity, Details, ExtendedDetails
    , Agent, MessageCode, AgentVersion
| extend Dummy= ''
| extend AlertRuleUniqueName = 'datacollectionhealthcheck'

let Role = 'Production';
let AuditClasses = dynamic(['CUL']); // Audit Log Classes - Debug Change
let allSystemRoles = dynamic(['Sandbox','Developement','QualityAssurance','Training','Production']); // Available System Roles
let allSystemUsage = dynamic (['ERP','CRM','BW','Solman','Gateway','Enterprise Portal']); // Available System Usages
// Get Relevant Systems from WatchList
let systemID = _GetWatchlist('SAP - Systems');
let fixedSID = datatable(SystemID:string, SystemRole:string, SystemUsage:string)
// Maintain these if WatchList is not available
    ["S4H","Production","ERP",
    "XXX","Sandbox","BW"]
;
let UnitedSystem =
union systemID, fixedSID
| summarize by SystemID, SystemRole, SystemUsage
| where SystemRole == Role; // Recommended  is Production only
//| where SystemRole in (allSystemRoles); // Use this for all system roles
SAPAuditLog
| where MessageID in (AuditClasses)
| project-rename SystemIDTemp = SystemID
| project-rename SystemID= SystemIDTemp
| project-rename SystemID= SystemID
| lookup kind = inner    (UnitedSystem) on SystemID
| extend  ChangeDetails = pack_array(Variable1, Variable2, Variable3, Variable4)
| project TimeGenerated,ClientID, Instance, User, MessageText, ChangeDetails, ABAPProgramName, TransactionCode, SystemID, SystemRole, SystemUsage, Email,TerminalIPv6, Host
| extend AlertRuleUniqueName = 'datahaschangedduringdebuggingactivity'

// Audit Log Classes - Audit Log Active Status Events
let AuditClasses = dynamic(['AUJ']);
SAPAuditLog
| where MessageID in (AuditClasses)
| where Variable1 == '0' // Audit Active Status = 0
| project
// Details
TimeGenerated, Instance ,SystemID, ClientID, User, MessageText, Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'deactivationofsecurityauditlog'

// Define variables
// here you can exclude system users which are OK to execute a Dynamic ABAP Program
// by adding those users in the SAP_User_Config watchlist with a tag of 'DynamicABAPProgramOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['DynamicABAPProgramOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let AuditClasses = dynamic(['BU4']); // Message of dynamic ABAP program
let TranForABAPGen = _GetWatchlist("SAP - Transactions for ABAP Generations"); // Transactions to exclude
// Maintain if watchlist is not available
let fixedTran = datatable(TransactionCode:string)['SE11','SE12', "SE16", "SE16N"];
let UnitedTransactions =
toscalar(union TranForABAPGen, fixedTran
| summarize Transactions = make_set(TransactionCode));
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| where TransactionCode !in (UnitedTransactions) // Exclude transactions
| project TimeGenerated, Instance, User, SystemID, ClientID, Email, TerminalIPv6, Host, ABAPProgramName
| extend AlertRuleUniqueName = 'dynamicabapprogram'

// Sentinel4SAP solution version 1.2.98
// populate static selections for workspace level configuration
// time span for audit events
let AuditTimeAgo= 65m;
let SelectedSystems =  dynamic(["All Systems"]);//can also do// let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do//let SelectedSeverities =  dynamic(["All Severities"])
let SelectedRuleTypes= dynamic([ "Deterministic"]); // Rule only goes for deterministic alerting
// users that are tagged with these tags in the SAP_User_Config WL will produce high severity incidents on all activities
let SpecialFocusTags=dynamic(["SoonToLeave"]); // can also do dynamic(["SoonToLeave", "Compromised"])
let AuditConfiguration= materialize(SAPAuditLogConfiguration(SelectedSystems, SelectedSystemRoles, SelectedSeverities= dynamic(["All Severities"]), SelectedRuleTypes= SelectedRuleTypes));
// get special users for exclusion and in-focus analysis
let SAPUsersGottenVIP = (SAPUsersGetVIP(SpecialFocusTags= SpecialFocusTags) | project-rename User = SAPUser);
let SAPUserRolesAndProfiles = (SAPUsersAssignments | project-keep User, SystemID, DirectRoles, ChildRoles, Profiles | summarize RolesAndProfiles = make_set(array_concat(DirectRoles, ChildRoles, Profiles), 120) by User, SystemID | where SystemID in ((AuditConfiguration | summarize  by SystemID)));
let AuditEvents = (SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| where SystemID in ((AuditConfiguration | summarize  by SystemID))
| where MessageID in ((AuditConfiguration | summarize  by MessageID))
// Alerts coming from SAP as "Low" aren't very intersting
| where AlertSeverityText != "Low"
// get the configuration from the "SAPAuditLogConfiguration" watchlist which allows
// assigning severity levels per system Role (Production/ non-production), automatic team assignments and other configuration options
// we'll fetch all severities to allow for alerting on "Special Focus" users regardless of severity
| lookup kind=inner AuditConfiguration on
$left.MessageID == $right.MessageID
,$left.SystemID == $right.SystemID
);
// Query
AuditEvents
// get the user privleged status
| lookup (SAPUsersGetPrivileged() | extend IsPrivilegedUser = true) on
$left.SystemID == $right.SystemID
,$left.User == $right.User
,$left.ClientID == $right.Client
| extend DetailedDescription = iff(IsPrivilegedUser, strcat('User ', User,  ' is a privileged user! <br/>', DetailedDescription), DetailedDescription)
| extend BagOfDetails = tostring(BagOfDetails)
// handle threshold per system (configurable via the SAPAuditLogConfigurator watchlist)
// calculate the timespan for analysis for thresholding
| summarize EventCount= count(),  Threshold = max( Threshold), MinTime= min(TimeGenerated), MaxTime = max(TimeGenerated)
by AuditClassID, AlertSeverityText, SystemID, Instance, MonitoringObjectName, MonitorShortName, MessageText, MessageClass, MessageID, AlertValue, AlertSeverity, ClientID, User, Variable1, Variable2, Variable3, Variable4, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, SystemNumber, Host, Type, CategoryName, DestinationEmail, DetailedDescription, Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, IsPrivilegedUser, RolesTagsToExclude
| extend  TimeGenerated = MaxTime
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
// To reduce false positives, exclude users whos tags in the SAP_User_Config watchlist
// match tags in the RolesTagsToExclude column in the SAP_Dynamic_Audit_Log_Monitor_Configuration
// tags can be either strings representing a group such as Batch; Sensitive; Super; MassiveLogonsOK; MassiveRFCOK; GenericTablebyRFCOK
// or SAP roles such as SAP_BC_TRANSPORT_ADMINISTRATOR, or SAP profiles like SAP_ALL
| lookup SAPUsersGottenVIP on User
| lookup SAPUserRolesAndProfiles on User, SystemID
| extend RolesProfilesAndTags = array_concat(RolesAndProfiles, TagsList)
| extend TagsIntersect = set_intersect(split(RolesTagsToExclude,";"), RolesProfilesAndTags)
| extend IntersectionSize = array_length(TagsIntersect)
// keep only non-excluded users that are not under "Special Focus"
| where IntersectionSize == 0 or isempty(IntersectionSize) or SpecialFocusTagged == true
| extend DetailedDescription = iff(SpecialFocusTagged, strcat('User ', User,  ' is a specially tagged User with tags ', TagsList ,'<br/>', DetailedDescription), DetailedDescription)
// Special Focus users production alerts are considered as high
| extend Severity = iff(SpecialFocusTagged,'High',Severity)
// | where SelectedSeverities contains Severity
//  apply the threshold criteria, consider that the watchlist describes hourly thresholds
| extend MinutesSpan = datetime_diff('Minute', MaxTime, MinTime )
// | where EventCount > (Threshold * max_of(MinutesSpan, 10) / 60) or SpecialFocusTagged == true
| extend DetailedDescription = iff(EventCount > 1, strcat(' Event occured ', EventCount, ' times within ', MinutesSpan + 1, ' minutes.<br/> ', 'The predefined threshold of ', Threshold,  ' Events per hour was exceeded! <br/>', DetailedDescription), DetailedDescription)
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| project-keep AuditClassID, AlertSeverityText, SystemID, Instance, MessageText, MessageClass, MessageID, AlertValue, AlertSeverity, ClientID, User, Variable1, Variable2, Variable3, Variable4, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, SystemNumber, Host, CategoryName, DestinationEmail, DetailedDescription, Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, IsPrivilegedUser, EventCount, Threshold, TimeGenerated
| extend AlertRuleUniqueName = 'dynamicdeterministicauditlogmonitor'

// SAP - Execution of Sensitive Function Module
// Define variables
let RelTable = "EUFUNC"; // Relevant table
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
let SenseModules =  _GetWatchlist('SAP - Sensitive Function Modules');
let fixedModules = datatable(FunctionModule:string)['RSAU_CLEAR_AUDIT_LOG','BAPI_USER_CREATE', 'RFC_GET_TABLE_ENTRIES'];
let UnitedModules =
toscalar(union fixedModules, SenseModules
| summarize FunctionModules =  make_set(FunctionModule));
// here you can exclude system users which are OK to execute a Sensitive function module
// by adding those users in the SAP_User_Config watchlist with a tag of 'ExecuteSensitiveFMOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['ExecuteSensitiveFMOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query logic
let LastLogin = SAPAuditLog
| where MessageID in (AuditLogIn)
| summarize TimeGenerated = arg_max(TimeGenerated, *) by SystemID, ClientID, User; // Get last connection date for user
SAPTableDataLog
| where TableName == RelTable
| extend Module = extract(@"\s+(.{1,}\b)\s+", 1, LogKey, typeof(string)) // Extract Function Module
| extend FunctionGroup = extract(@"^FL([^\s]*)\s", 1, LogKey, typeof(string)) // Extract Function Group
| extend ExecutionVariant = extract(@"\b(\w+)$", 1, LogKey, typeof(string)) // Extract Execution Varient
| where Module in (UnitedModules) // Module is sensitive
| lookup kind=inner LastLogin on $left.UserName == $right.User
| join kind=leftantisemi excludedUsers on $left.UserName == $right.User2Exclude
| project TimeGenerated, Instance ,SystemID, UserName, ClientID, Email, TerminalIPv6, Host, Module, FunctionGroup, ExecutionVariant
| extend AlertRuleUniqueName = 'executionofsensitivefunctionmodule'

// SAP - Execution of a Sensitive ABAP Program
// this alert rule requires the "look back" period to be greater than 2 days to include a full user master data load
// the actual lookback period for the events is determined using the TimeAgo parameter
let TimeAgo= 60m;
// determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// here you can exclude system users which are OK to execute a Sensitive ABAP Program
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveABAPProgramOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['SensitiveABAPProgramOK','SAPRole','SAPProfile']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Get Relevant ABAP Programs, along with users which are OK to execute them. if the 'SAP - Sensitive ABAP Programs' watchlist is missing the UsersExcluded column,
// add the column manually, or force this watchlist to refresh using a fresh install of the solution, using the
let SensitiveABAPReports = _GetWatchlist('SAP - Sensitive ABAP Programs')
| extend UsersExcluded= replace_string(column_ifexists("UsersExcluded",""), " ","")
| extend UsersExcluded= parse_csv(UsersExcluded)
| summarize UsersExcluded= make_set(UsersExcluded, 1000), Description=take_anyif(Description, isnotempty(Description)) by ABAPProgramName= SearchKey;
let fixedABAPReports = datatable(ABAPProgramName:string, Description:string , UsersExcluded: dynamic)
// Maintain these if WatchList is not available
    ["RSPFLDOC", "Profile Parameter Maintenance", "DummyUser"];
let UnionAbap =
    union SensitiveABAPReports, fixedABAPReports
    | summarize UsersExcluded= make_set(UsersExcluded, 1000), Description=any(Description)  by ABAPProgramName;
// Query logic
SAPAuditLog
| where TimeGenerated > ago(TimeAgo + 1h)
| where MessageID == 'AUW'
| where ingestion_time() > ago(TimeAgo)
| join kind= inner UnionAbap on ABAPProgramName
| where UsersExcluded !has User
| where SystemID in ((SelectedSystems | project SystemID))
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| order by TimeGenerated asc
| project TimeGenerated, Instance , SystemID, ClientID, User, ABAPProgramName, MessageText, TransactionCode, MessageID, Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'executionofasensitiveabapprogram'

// SAP - Execution of a Sensitive Transaction Code
// Define Variables
// the actual lookback period is 1h. The alert looks back further to allow for user master data to be built slowly
let TimeAgo= 75m;
// let AuditClasses = dynamic(['AU3']); // Audit Log Classes - Transaction Started
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey, SystemRole;
// Get Relevant Transaction Codes
let SensitiveTcode = _GetWatchlist('SAP - Sensitive Transactions') | summarize by TransactionCode;
// here you can exclude system users which are OK to execute a Sensitive Transaction Code
// by adding those users in the SAP_User_Config watchlist with a tag of 'ExecuteSensitiveOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['ExecuteSensitiveOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query logic
SAPAuditLog
| where ingestion_time() > ago(TimeAgo)
| where MessageID == 'AU3'
| join kind=inner SelectedSystems on SystemID
| where TransactionCode in (SensitiveTcode) or Variable1 in (SensitiveTcode)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| order by TimeGenerated asc
| project TimeGenerated,Instance ,SystemID, ClientID, User, TransactionCode, MessageText, MessageID, Email, TerminalIPv6, Host, TCODE=Variable1, SystemRole
| extend AlertName= strcat("SAP - ", replace_string(MessageText, ".",""), " by user ", User," in a ", tolower(SystemRole), " system")
| extend AlertDescription= strcat(iff(TCODE in (SensitiveTcode), "Sensitive transaction code ", "Transaction code "), TCODE,
" was started from ", iff(TransactionCode in (SensitiveTcode), " the sensitive context of ", " from the context of "),TransactionCode, ". Sensitive transactions are being maintained in the 'SAP - Sensitive Transactions' watchlist.")
| extend Dummy= ""
| extend AlertRuleUniqueName = 'executionofasensitivetransactioncode'

// Execution of Obsolete or Insecure Function Module
// AUK - Successful RFC call &C (function group = A) (C)
// AUL - Failed RFC call &C (function group = &A)  (C)
// DUI - RFC callback executed (destination &A, called &B, callback &C) (B)
// DUJ - RFC callback rejected (destination &A, called &B, callback &C) (B)
// DUK - RFC callback rejected (destination &A, called &B, callback &C) (B)
// DUR - JSON RPC call of function module &A succeeded  (A)
// DUS - SON RPC call of function module &A failed  (A)
// DUT - Critical JSON RPC call of function module &A (S_RFC * authorization) (A)
// EUE - RFC function module &A called successfully (A)
// EUF - Could not call RFC function module &A  (A)
// EUG - User does not have authorization to run RFC function module &A (A)
// FU1 - FC function &B with dynamic destination &C was called in program &A (B)
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// here you can exclude system users which are OK to run obolete/ sensitive function modules
// by adding those users in the SAP_User_Config watchlist with a tag of 'RunObsoleteFMOK'
let excludeUsersTags= dynamic(['RunObsoleteFMOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
let AuditClassesA= dynamic(['DUR', 'DUS', 'DUT', 'EUE', 'EUF', 'EUG']);
let AuditClassesB= dynamic(['DUI', 'DUJ', 'DUK', 'FU1']);
let AuditClassesC= dynamic(['AUK', 'AUL']);
let ObsoleteModules =  _GetWatchlist('SAP - Obsolete Function Modules');
// Maintain if watchlist is not available
let fixedModules = datatable(FunctionModule:string)['TH_SAPREL','TH_SAPREL2', 'TH_SAPREL3','SUSR_RFC_USER_INTERFACE','RFC_ABAP_INSTALL_AND_RUN'];
let UnitedModules =
toscalar(union fixedModules, ObsoleteModules
| summarize FunctionModules =  make_set(FunctionModule));
// Query logic
let FunctionRuns =
SAPAuditLog
| where MessageID in (array_concat(AuditClassesA, AuditClassesB, AuditClassesC))
| join kind= inner SelectedSystems on SystemID
| extend Module = case(MessageID in (AuditClassesA), Variable1, MessageID in (AuditClassesB), Variable2, MessageID in (AuditClassesC), Variable3, 'Could Not find function module')
| where Module in (UnitedModules);
FunctionRuns
| project TimeGenerated, SystemID, ClientID, User, Email= iff(isempty(Email),User, Email), TerminalIPv6, Host= iff(isempty(Host), TerminalIPv6, Host), Module
| extend UserName= User
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| extend AlertDescription= strcat('Sensitive Function Module ' , Module, ' was executed by ', UserName), Dummy=' '
| extend PackedDetails= pack_all()
| extend AlertRuleUniqueName = 'executionofanobsoleteoraninsecurefunctionmodule'

// Execution of Obsolete/Insecure Program
// Define Variables
let AuditClasses = dynamic(['AUW']); // Audit Log Classes - Report Started
let ObsoletePrograms = _GetWatchlist("SAP - Obsolete Programs");
// Maintain if watchlist is not available
let fixedObPrograms = datatable(ABAPProgram:string)['RSAU_READ_LOG'];
let UnitedPrograms =
toscalar(union fixedObPrograms, ObsoletePrograms
| summarize Programs = make_set(ABAPProgram));
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles=SelectedSystemRoles) | project SystemID= SearchKey;
// here you can exclude system users which are OK to run obsolete/ sensitive programs
// by adding those users in the SAP_User_Config watchlist with a tag of 'RunObsoleteProgOK'
let excludeUsersTags= dynamic(['RunObsoleteProgOK']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= dynamic(["RunObsoleteProgOK"]))| summarize by User2Exclude=SAPUser;
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| where ABAPProgramName in (UnitedPrograms) // The program is obsolete
| join kind= inner  SelectedSystems on SystemID
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project TimeGenerated, Instance,SystemID, ClientID, User, ABAPProgramName, MessageText, TransactionCode, MessageID
, Email= iff(isempty(Email),User, Email), TerminalIPv6, Host= iff(isempty(Host), TerminalIPv6, Host)
| extend AlertDescription= strcat('Obsolete/ Insecure Program ' , ABAPProgramName, ' was executed by ', User), Dummy=' '
| extend PackedDetails= pack_all()
| extend AlertRuleUniqueName = 'executionofanobsoleteoraninsecureprogram'

// Define variables
let AuditClasses = dynamic(['DU8']); // Message of Successfull FTP Connection
let FTPSafeConnections = _GetWatchlist("SAP - FTP Servers");
let fixedConnections = datatable(Client: string, Description: string,
FTP_Server_Name: string, FTP_Server_Port: string)
    // Maintain these if WatchList is not available
    [100, "description", "http://ourorgftpserver.com/", 22]
;
let UnitedConnections = toscalar(union kind=outer isfuzzy=true FTPSafeConnections, fixedConnections | summarize Connections = make_set(FTP_Server_Name));
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| extend TerminalIPv6 = Variable1
| where TerminalIPv6 !in (UnitedConnections) // The IP is unknown
| project TimeGenerated, Instance ,User, SystemID, ClientID, Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'ftpfornonauthorizedservers'

let Role = 'Production';
let ProgramName = 'RS_TESTFRAME_CALL';
let AuditClasses = dynamic(['AUW']); // Audit Log Classes - Report Started
let allSystemRoles = dynamic(['Sandbox', 'Developement', 'QualityAssurance', 'Training', 'Production']); // Available System Roles
let allSystemUsage = dynamic (['ERP', 'CRM', 'BW', 'Solman', 'Gateway', 'Enterprise Portal']); // Available System Usages
// Get Relevant Systems from WatchList
let systemID = _GetWatchlist('SAP - Systems');
let fixedSID = datatable(SystemID: string, SystemRole: string, SystemUsage: string)
    // Maintain these if WatchList is not available
    ["S4H", "Production", "ERP",
    "XXX", "Sandbox", "BW"]
;
let UnitedSystem =
union systemID, fixedSID
| summarize by SystemID, SystemRole, SystemUsage
| where SystemRole == Role; // Reccommended is Production only
//| where SystemRole in (allSystemRoles); // Use this for all system roles
SAPAuditLog
| where MessageID in (AuditClasses)
| where ABAPProgramName == ProgramName
| project-rename SystemIDTemp = SystemID
| project-rename SystemID= SystemIDTemp
| project-rename SystemID= SystemID
| lookup kind = inner (UnitedSystem) on SystemID
| order by TimeGenerated asc
| project TimeGenerated, Instance ,User, SystemID, ClientID, MessageText, MessageID,
Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'functionmoduletested'

// Define variables
let VarNote = "NOTE";
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
// Query logic
let LastLogin =
SAPAuditLog
| where MessageID in (AuditLogIn)
| summarize TimeGenerated = arg_max(TimeGenerated, *) by SystemID, ClientID, User; // Get last connection date for user
SAPCRLog
| where ObjectType == VarNote // Type is NOTE
| lookup kind=inner LastLogin on $left.Owner == $right.User // Get last login details
| summarize by TimeGenerated, Instance, SystemID, SystemNumber, ClientID, Owner, Request, Description, ObjectName, Category, Status, Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'lifecycle-sapnoteswereimplementedinsystem'

let AuditClasses = dynamic(['AU1','AU5']); // Audit Log Classes - Dialog Logon Successful, RFC Logon Successful
// Dialog / CPIC / RFC Int / RFC Ext / SRFC / User Switch / HTTP / Restore Session / API Call
let DialogLogonTypes = dynamic(['A', 'C', 'F', 'R', 'S', 'U', 'H', 'u', ' ']);
let Networks = _GetWatchlist('SAP - Networks');
let fixedNetworks = datatable(Network: string)['111.68.128.0/17']; // Maintain these if watchlist is not available
let allNetworks = union Networks, fixedNetworks
    | summarize by Network;
SAPAuditLog
// Add audit classes
| where MessageID in (AuditClasses)
| where Variable1 in (DialogLogonTypes) // Is a dialog logon type from the list
| where isnotempty(TerminalIPv6) // There is a Ipv6 address
// if you wish to allow all private adreesses
| where not (ipv4_is_private(TerminalIPv6 ))
| evaluate ipv4_lookup(allNetworks, TerminalIPv6, Network, return_unmatched = true)
// Similar to regular lookup, by ipv4 address, unmatched is like left join
| where isempty(Network) // Network is not familiar
// Details
| project TimeGenerated, SystemID, ClientID, User, TransactionCode, MessageText, Email , TerminalIPv6, Host
| extend AlertRuleUniqueName = 'loginfromunexpectednetwork'

// SAP - Medium - Multiple Logons by IP
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let TimeAgo= 75m;
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// here you can exclude system users which are OK to share IP addresses
// by adding those users in the SAP_User_Config watchlist with a tag of 'MultipleLogonsOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['MultipleLogonsOK', 'SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)
    | summarize by User2Exclude=SAPUser;
// CONFIG OPTION - include users by types (["A", "Dialog", "B", "System", "C", "Communications Data", "L", "Reference (Logon not possible)", "S", "Service"])
let UsersTypeToInclude= dynamic(["A"]);// Dialog Only
// CONFIG OPTION - exclude users by user classes
let UserClassesToExclude= dynamic(["RFC", "BATCH"]);
let UserInFocus= (SAP_USR02
| where CLASS !in (UserClassesToExclude)
// | where USTYP in (UsersTypeToInclude)
| summarize arg_max(TimeGenerated, *) by GLTGB, SystemID, User=BNAME, ClientID= MANDT, UserType=USTYP
| summarize arg_max(GLTGB, CLASS) by SystemID, User, ClientID, UserType);
let AuditClasses = dynamic(['AU1', 'AU5']); // Audit Log Classes - Dialog Logon Successful, RFC Logon Successful
let DialogLogonTypes = dynamic(['A', 'C', 'F', 'R', 'S', 'U', 'H', 'u', ' ']); // Dialog / CPIC / RFC Int / RFC Ext / SRFC / User Switch / HTTP / Restore Session / API Call
let excNetworks = materialize(_GetWatchlist('SAP - Excluded Networks')); // Networks that should be removed from query
// add an entry with serachkey= 000.00.000.0 and Description= EmptyIPAddressesShouldBeExcluded in the 'SAP - Excluded Networks' watchlist
// to avoid alerting on empty IP addresses
let EmptyIPAddressesShouldBeExcluded = toscalar(excNetworks
    | where Description == 'EmptyIPAddressesShouldBeExcluded'
    | summarize Count =count()
    | project IfEmptyIPAddressesShouldBeExcluded = iff(Count > 0, true, false));
let fixedNetworks =
    datatable(Network: string) [
    "255.68.255.0/32", ""];
let UnitedNetworks = union excNetworks, fixedNetworks
    | summarize by Network
    | where isnotempty(Network)
    | extend DummyJoinField= 1;
let UsersPerIP = 1;
// Query logic
SAPAuditLog
| where TimeGenerated > ago(TimeAgo + 1h)
| where ingestion_time() > ago(TimeAgo)
| where SystemID in (SelectedSystems)
// rule only cares for audit records with IP addresses
| where isnotempty(TerminalIPv6) or EmptyIPAddressesShouldBeExcluded == false
// exclude users by type/ class
| join kind= inner UserInFocus on SystemID, ClientID, User
//comment the next line if private IP addresses are relevant
| where not (ipv4_is_private(TerminalIPv6 ))
| where MessageID in (AuditClasses)
| where Variable1 in (DialogLogonTypes)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| extend DummyJoinField= 1
| join kind= inner UnitedNetworks on DummyJoinField
// check if IP is in one of the ranges of the watchlist
| extend IPIsInRange= ipv4_is_in_range(TerminalIPv6, Network)
| extend UserandEmail = pack("ID", User, "Email", Email)
// count cases in which an IP address shows in any of the ranges in the watchlist
| summarize CountUsers = dcount(strcat(User, "_&_", Email)), Users = make_set(UserandEmail), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated)
    , IPIsInRange= countif(IPIsInRange)
    by SystemID, ClientID, TerminalIPv6
// filter out matches of IPs and excluded networks
| where IPIsInRange == 0
| where CountUsers > UsersPerIP
| mv-expand Users
| evaluate bag_unpack(Users, "User_")
| project SystemID, ClientID, TerminalIPv6, StartTime, EndTime,
    User = column_ifexists("User_ID", ""),
    Email = column_ifexists("User_Email", "")
| extend Email= iff(isempty(Email), User, Email)
| lookup UserInFocus on User, SystemID, ClientID
| extend AlertRuleUniqueName = 'multiplelogonsbyip'

// SAP - Multiple Subnets by User (previously named as SAP - Multiple Logons by User)
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
// Define variables
// CONFIG OPTION time ago is same as lookback
let TimeAgo= 1h;
// CONFIG OPTION Mask is a number between 0 and 32 representing sensitivity to network changes
// Mask= 32 means '192.168.1.255' and '192.168.1.254' are different addresses.
// Mask= 24 means '255.255.255.0' and '255.255.255.255' are considered as same address.
let Mask= 24;
let AuditClasses = dynamic(['AU1', 'AU5']); // Logon successful, RFC/CPIC logon successful
// CONFIG OPTION - include users by types (["A", "Dialog", "B", "System", "C", "Communications Data", "L", "Reference (Logon not possible)", "S", "Service"])
let UsersTypeToInclude= dynamic(["A"]);// Dialog Only
// CONFIG OPTION - exclude users by user classes
let UserClassesToExclude= dynamic(["SYSTEM"]);
let UserInFocus= (SAP_USR02
| where CLASS !in (UserClassesToExclude)
| where USTYP in (UsersTypeToInclude)
| summarize arg_max(TimeGenerated, *) by GLTGB, SystemID, User=BNAME, ClientID= MANDT, UserType=USTYP
| summarize arg_max(GLTGB, *) by SystemID, User, ClientID, UserType);
// Dialog / CPIC / RFC Int / RFC Ext / SRFC / User Switch / HTTP / Restore Session / API Call
let DialogLogonTypes = dynamic(['A', 'C', 'F', 'R', 'S', 'U', 'H', 'u', ' ']);
let excUsers = _GetWatchlist('SAP - Excluded Users'); // Users that should be removed from query
let fixedExcUsers = datatable(User: string) ["SYSWF"];
// CONFIG OPTION- exclude user by SAP roles, profiles or tags
// here you can exclude system users which are OK to logon from multiple IPs
// by adding those users in the SAP_User_Config watchlist with a tag of 'MultipleIPsOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['MultipleIPsOK', "SAPRole1", "SAPProfile1"]);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let UnitedExcUsers =
    toscalar(union excUsers, fixedExcUsers
    | summarize Users = make_set(User));
// CONFIG OPTION- set the threshold of the number of unique IP addresses per user
let IPThreshold = 1;
// CONFIG OPTION- exclude networks (or ranges) using the watchlist
let ExcNetworks = toscalar(_GetWatchlist('SAP - Excluded Networks') // Networks that should be removed from query
| where isnotempty(Network)
| summarize make_set(Network));
// CONFIG OPTION- exclude blank IP addresses
// add an entry with serachkey= 000.00.000.0 and Description= EmptyIPAddressesShouldBeExcluded in the 'SAP - Excluded Networks' watchlist
// to avoid alerting on empty IP addresses
let EmptyIPAddressesShouldBeExcluded = toscalar(_GetWatchlist('SAP - Excluded Networks')
| where Description == 'EmptyIPAddressesShouldBeExcluded' and SearchKey == "000.00.000.0" | take 1
| project IfEmptyIPAddressesShouldBeExcluded = true);
// Query Logic
let LogOns= materialize(SAPAuditLog
| where TimeGenerated > ago(1d)
// actual lookback is set by setting TimeAgo
| where ingestion_time() > ago(TimeAgo)
| where MessageID in (AuditClasses)
| where isnotempty(TerminalIPv6) or  EmptyIPAddressesShouldBeExcluded != true
| where ipv4_is_in_any_range(TerminalIPv6, ExcNetworks) == false
| where Variable1 in (DialogLogonTypes)
| where User !in (UnitedExcUsers)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| join kind= inner UserInFocus on SystemID, ClientID, User
| summarize LogonCount= count(), StartTime = min(TimeGenerated), UserType= any(UserType)
    ,EndTime = max(TimeGenerated), Email= any(Email), Instance= any(Instance), Host= any(Host) by SystemID, ClientID, User, TerminalIPv6) ;
// aggregate the number of subnets (not IP addresses) by user
let UsersWithTooManySubnets= LogOns
| extend MaskedIP= format_ipv4_mask(TerminalIPv6, Mask)
| summarize CountSubnets= dcount(MaskedIP) by User
| where CountSubnets > IPThreshold;
UsersWithTooManySubnets | join kind = inner LogOns on User
| project SystemID, ClientID, User, StartTime, EndTime, Email, TerminalIPv6, Instance, LogonCount, Host, CountSubnets, UserType
| extend PackedDetails= pack_all()
| extend AlertName= strcat('SAP - User ',User, ' has logged on from ', CountSubnets, ' different subnets'), Dummy= ''
| extend AlertRuleUniqueName = 'multiplesubnetsbyuser'

// SAP - Security Audit Log Configuration Change
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
let TimeAgo= 75m;
// Audit Log Classes - Audit Log Configuration Events
// determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["Production", "UAT"]); //can also do// let SelectedSystemRoles =  dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage;
// here you can exclude system users which are OK to execute a configuration change
// by adding those users in the SAP_User_Config watchlist with a tag of 'AuditLogConfigOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['AuditLogConfigOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let AuditClasses = dynamic(['AUE', 'AUF', 'AUI', 'AUJ', 'FU0', 'E05', 'AUG']); // Relevant messages
// gather all audit log configuration changes
let ConfigChanges= materialize(SAPAuditLog
| where TimeGenerated > ago(TimeAgo)
| where MessageID in (AuditClasses)
| where SystemID in ((SelectedSystems | project SystemID))
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project TimeGenerated, Instance, SystemID, User, MessageText, Email, TerminalIPv6, Host, MessageID, UpdatedOn_t);
let SystemRestarts= ConfigChanges | where MessageID == "AUG"
| distinct SystemID, Instance, Host, UpdatedOn_t;
ConfigChanges
// disregard changes that got applied when system restarted
| join kind =leftanti SystemRestarts on SystemID, Instance, Host, UpdatedOn_t
| lookup (SelectedSystems | project SystemID, SystemRole) on SystemID
| summarize FirstChangedOn=min(UpdatedOn_t), LastChangedOn=max(UpdatedOn_t), Events=make_set(MessageText), SystemRole=any(SystemRole) by SystemID, User, Email, TerminalIPv6, BinTime= bin(UpdatedOn_t,1h), Host, Instance
| extend AlertName= strcat("SAP audit log configuration change detected in a ", tolower(SystemRole)," system")
| extend Dummy= ""
| extend AlertRuleUniqueName = 'securityauditlogconfigurationchange'

// SAP - Sensitive Privileged User Logged in
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
// time span for audit events
let AuditTimeAgo= 75m;
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey, SystemRole;
// here you can exclude system users which are OK to execute a Sensitive Transaction Code
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveLogOnOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['SensitiveLogOnOK']);// dynamic(['SensitiveLogOnOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Audit Log Classes - Dialog Logon Successful
let AuditClassesSuccess = dynamic(['AU1']);
let AuditClassesFail = dynamic(['AU2']);
let LogonTypes = dynamic(['A','H', 'R', 'S']); // Dialog / HTTP
let DataTypes = materialize(SAPGetDataTypes());
// Query logic
SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| join kind= inner SelectedSystems on SystemID
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| where (MessageID in (AuditClassesSuccess) and Variable1 in (LogonTypes)) or // Success login
MessageID in (AuditClassesFail)
|join kind = inner (SAPUsersGetPrivileged())
on $left.ClientID == $right.Client
, $left.SystemID == $right.SystemID
, $left.User == $right.User
| project-rename LogonTypeKey = Variable1, ReasonKey= Variable2, LogonMethodKey= Variable3
| lookup (DataTypes | where DataType == "LogonFailureCause"| project Key, LogonFailureCause= Value) on $left.ReasonKey == $right.Key
| lookup (DataTypes | where DataType == "LogonType"| project Key, LogonType= Value) on $left.LogonTypeKey == $right.Key
| lookup (DataTypes | where DataType == "LogonMethod"| project Key, LogonMethod= Value) on $left.LogonMethodKey == $right.Key
| project TimeGenerated, Instance ,SystemID, ClientID, LogonType, User, MessageText,
Email, TerminalIPv6, Host, SystemRole, LogonFailureCause, LogonMethod, MessageID
| extend PackedDetails= pack_all()
| extend AlertName= strcat("SAP - Privileged User ", User, iff(MessageID=="AU1"," Has Logged into a ", " Has Failed to Log into a "), SystemRole, " System" )
| extend AlertDescription= strcat(" LogonType: ", LogonType, ". LogonFailureCause: ", LogonFailureCause, ". LogonMethod: ", LogonMethod), Dummy= ''
| extend AlertRuleUniqueName = 'sensitiveprivilegeduserloggedin'

// Sensitive Roles Changes
// Define variables
let TimeAgo= 2h;
let RoleObjectClass = "PFCG";
let ChangeType = "U";
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
let SensitiveRoles = _GetWatchlist("SAP - Sensitive Roles");
// Maintain if watchlist is not available
let fixedRoles = datatable(Role: string)['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
let UnitedRoles =toscalar(union fixedRoles, SensitiveRoles
| summarize Roles = make_list(Role));
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles=SelectedSystemRoles) | project SystemID= SearchKey, SystemRole;
// here you can exclude system users which are OK to run sensitive role updates
// by adding those users in the SAP_User_Config watchlist with a tag of 'UpdateSensitiveAuthOK'
let excludeUsersTags= dynamic(['UpdateSensitiveAuthOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query logic
let RoleChanges= SAPChangeDocsLog
| where TimeGenerated> ago(TimeAgo+ 1h)
| where ingestion_time() > ago(TimeAgo)
| where TableName contains ("1251")
| where ObjectClass == RoleObjectClass // Relevant role object class
| where TypeofChange_Header == ChangeType
| join kind= inner  SelectedSystems on SystemID
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| extend AuthorizationObj = extract(@"\S+\s+(\S+)\s+", 1, ChangedTableKey, typeof(string)) // Extract Authorization Object
| extend Authorization = extract(@"\S+\s+\S+\s+(\S+\d+)", 1, ChangedTableKey, typeof(string)) // Extract Authorization
| where ObjectID in (UnitedRoles) // Role is sensitive
| project-rename ChangedOn= UpdatedOn_t
| extend BagOfChange= bag_pack("Authorization",Authorization, "AuthorizationObj", AuthorizationObj, "FieldName", FieldName, "OldValue", ValueOld,"NewValue", ValueNew )
| summarize TableName=any(TableName), TransactionCode=any(TransactionCode), SystemRole=any(SystemRole), Host=any(Host), Instance=any(Instance), Authorizations= makeset(Authorization), BagOfChanges= make_set(BagOfChange, 30) by ChangedOn , SystemID, ClientID, ChangedRole= ObjectID, User;
let RoleChangesEarliest= toscalar(RoleChanges | summarize EarliestChange= min(ChangedOn));
let Logins = SAPAuditLog
| where TimeGenerated> RoleChangesEarliest - 1h
| where MessageID in (AuditLogIn)
| where User in((RoleChanges | distinct User))
| project-rename LoggedOn= UpdatedOn_t
| summarize count() by Email, TerminalIPv6, User, SystemID, ClientID, bin(LoggedOn, 1h); // Get last connection date for user
RoleChanges
| lookup kind=inner Logins on User, ClientID, SystemID // Get IP
| where ChangedOn > LoggedOn
| extend ChangeKey=strcat(ChangedRole, User, SystemID, ChangedOn)
| order by ChangeKey
| extend UpdatedOnDiff= datetime_diff('minute', ChangedOn, LoggedOn)
| extend Rank=row_number(1, prev(ChangeKey) != ChangeKey)
| where Rank< 3 // allow up to 2 IP addresses per 1h for capture replay scenarios
| project Authorizations, TimeGenerated= ChangedOn, Instance, SystemID, ClientID, Email, User, TerminalIPv6, Host, ChangedRole, BagOfChanges, SystemRole
| extend Email= iff(isempty(Email),User, Email), Host= iff(isempty(Host), TerminalIPv6, Host)
| extend AlertName= strcat('Sensitive Role ' , ChangedRole, ' was updated by ', User, " in a ", tolower(SystemRole), " system"), Dummy=' '
| extend PackedDetails= pack_all()
| extend AlertDescription= strcat(" The sensitive role ", ChangedRole, " was changed by user ", User,". Changed authorizations: ", Authorizations,". The list of changes detected: ", BagOfChanges)
| extend AlertRuleUniqueName = 'sensitiverolechanges'

alertDetailsOverride.alertDescriptionFormat:
