Sensitive roles should be maintained in "SAP - Sensitive Roles" Watchlist

*Data Sources: SAPcon - Change Documents Log*
{{PackedDetails}}

properties.query:
// Define variables
let Role = "Production";
let AuditClasses = dynamic(['DU9']); // Dialog, Audit Log Classes - Generic Table Access
let allSystemRoles = dynamic(['Sandbox','Developement','QualityAssurance','Training','Production']); // Available System Roles
let allSystemUsage = dynamic (['ERP','CRM','BW','Solman','Gateway','Enterprise Portal']); // Available System Usages
// Get Relevant Systems from WatchList
let systemID = _GetWatchlist('SAP - Systems');
let fixedSID = datatable(SystemID:string, SystemRole:string, SystemUsage:string)
// Maintain these if WatchList is not available
    ["S4H","Production","ERP",
    "XXX","Sandbox","BW"]
;
// Get Relevant Tables
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables');
let fixedTables = datatable(Table:string)
// Maintain these if WatchList is not available
    ["USR02"]
;
let RelSystemID = union systemID, fixedSID // Create a variable that stores relevant Systems
| where SystemRole == Role // Recommended  is Production only
//| where SystemRole in (allSystemRoles); // Use this for all system roles
| summarize by SystemID;
let SensitiveUnionTables = union SensitiveTables, fixedTables // Create a variable that stores relevant sensitive tables
    | summarize by Table;
// Query logic
SAPAuditLog
    | project-rename Table = Variable1, Activity = Variable2
    | where MessageID in (AuditClasses)
    | where SystemID in (RelSystemID)
    | where Table in (SensitiveUnionTables)
    | order by TimeGenerated asc
    | project TimeGenerated, Instance, SystemID, ClientID, User, TransactionCode, ABAPProgramName, Table,   Activity, MessageText, MessageID,
Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'sensitivetablesdirectaccessbydialoglogon'

// SAP - Sensitive Tables Direct Access By RFC Logon
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]); dynamic(["All System Roles"])
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles) | project SystemID= SearchKey;
// Get Relevant Tables
let SensitiveTables = _GetWatchlist('SAP - Sensitive Tables')| project Table;
// here you can exclude system users which are OK to access sensitive tables via RFC
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveTablebyRFCOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['SensitiveTablebyRFCOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query logic
SAPAuditLog
    | where SystemID in (SelectedSystems)
    | where MessageID == 'CUZ'
    | where Variable1 in (SensitiveTables)
    | project-rename Table = Variable1, Activity = Variable2
    | join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
    | order by TimeGenerated asc
    | project TimeGenerated,Instance, SystemID, ClientID, User, TransactionCode, ABAPProgramName, Table, Activity, MessageText, MessageID,
Email, TerminalIPv6, Host
    | extend PackedDetails= pack_all()
    | extend AlertDescription= strcat('SAP - User ', User, ' has accessed sensitive table ', Table, ' using RFC'), Dummy=''
    | extend RemediationSteps= strcat("Consult with the SAP security team to determine if user ", User, " is OK to perform this action. If the activity is benign, assign the user with the SensitiveTablebyRFCOK tag in SAP_User_Config watchlist. Otherwise, lock the SAP user in system ", SystemID), Dummy= ''
| extend AlertRuleUniqueName = 'sensitivetablesdirectaccessbyrfclogon'

// SAP - High - Sensitive privileged user makes a change in another user
// time span for audit events
let AuditTimeAgo= 75m;
// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'BU2']);
// here you can exclude system users which are OK to modify other users' master data
// by adding those users in the SAP_User_Config watchlist with a tag of 'ChangeUserMasterDataOK'
let excludeUsersTags= dynamic(['ChangeUserMasterDataOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query Logic
SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| where MessageID in (AuditClasses)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| join kind= leftsemi  SAPUsersGetPrivileged on // The user that makes a change is a sensitive privileged user
$left.User == $right.User
,$left.SystemID == $right.SystemID
,$left.ClientID == $right.Client
| extend ChangedUser= iff(MessageID == "BU2", Variable2, Variable1)
| lookup  (SAPUsersEmail()| project ChangedEmail= Email, ChangedUser= User, SystemID, ClientID)
 on SystemID, ClientID, ChangedUser
| project TimeGenerated, Instance ,SystemID, ClientID, User, MessageText, MessageID,
     Email= iff(isempty(Email),User, Email), TerminalIPv6, Host= iff(isempty(Host), TerminalIPv6, Host), ChangedUser, ChangedEmail
| extend AlertRuleUniqueName = 'sensitiveprivilegedusermakesachangeinanotheruser'

// Audit Log Classes - System Change Configuration
// !!!IMPORTANT!!!
// please make sure "Lookup data from the last" parameter is greater than a full user master data update cycle
// please keep "Lookup data from the last" to be 7 days to allow coverage of user master data required
// actual lookback is set by setting TimeAgo
let TimeAgo= 20m;
// CONFIGURATION OPTION- determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["All System Roles"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// here you can exclude system users which are OK to perform this action
// by adding those users in the SAP_User_Config watchlist with a tag of 'SystemConfigOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['SystemConfigOK']); // can also do dynamic(['SystemConfigOK','SAP_ALL'])
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
let AuditClasses = dynamic(['EU1']); // Relevant message
SAPAuditLog
| where TimeGenerated > ago(TimeAgo)
| where MessageID in (AuditClasses)
| where SystemID in (SelectedSystems)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project
// Details
TimeGenerated,Instance, ClientID ,SystemID, User, TransactionCode, SoftwareComponent = Variable1, NewModifiabilityStatus = Variable2, MessageText,
Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'systemconfigurationchange'

// RFC Execution of a Sensitive Function Module
let TimeAgo= 60m;
// determine which system roles are relevant for this alert
let SelectedSystemRoles =  dynamic(["Production", "UAT"]); //can also do// let SelectedSystemRoles =  dynamic(["All System Roles"]);
let SelectedSystems = SAPSystems(SelectedSystemRoles= SelectedSystemRoles)
    | project SystemID= SearchKey, SystemRole, SystemUsage
    | extend SystemRole= replace_string(SystemRole, 'Unknown (Production)', 'Unknown');
// here you can exclude system users which are OK to execute a Sensitive function module
// by adding those users in the SAP_User_Config watchlist with a tag of 'ExecuteSensitiveFMOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['ExecuteSensitiveFMOK','SAP_ALL']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// this watchlist lists the sensitive function modules, along with users that are OK to run those.
// if you don't see a column named "UsersExcluded" in this WL, add one
let SensitiveFMs = _GetWatchlist('SAP - Sensitive Function Modules')
| extend UsersExcluded= replace_string(column_ifexists("UsersExcluded",""), " ","")
| extend UsersExcluded= parse_csv(UsersExcluded)
| summarize UsersExcluded= make_set(UsersExcluded, 1000), Description=take_anyif(Description, isnotempty(Description)) by SearchKey;
let SensitiveFMExecs= materialize(SAPAuditLog
| where TimeGenerated > ago(TimeAgo + 1h)
| where ingestion_time() > ago(TimeAgo)
| where MessageID == "AUK"
| where SystemID in ((SelectedSystems | project SystemID))
| where Variable3 in ((SensitiveFMs | summarize make_set(SearchKey)))
| join kind = inner SensitiveFMs on $left.Variable3 == $right.SearchKey
// exclude users which are OK to run the specific FM
| where UsersExcluded !has User
| project-rename FunctionModule = Variable3, FunctionGroup = Variable1
| order by TimeGenerated asc
| project TimeGenerated,Instance ,User, SystemID, ClientID, FunctionGroup, FunctionModule, Description, MessageID, Email, TerminalIPv6, Host);
let UsersExecuted= (SensitiveFMExecs | summarize make_set(User,1000));
// let's see if the auth was there yesterday
let UsersAuth= SAPUsersAuthorizations(SnapshotAge= 1d)
| where User in (UsersExecuted) and Field == "RFC_NAME" and SystemID in ((SelectedSystems | project SystemID))
| summarize EQs= make_set(EQs) by User, ClientID, SystemID;
let UserMDSystems= SAP_AGR_AGRS | where TimeGenerated > 1d | summarize by SystemID, ClientID= MANDT | extend IsUserMDThere= true;
// now we check if the user had permisions to perform the RFC call yesterday
let UnAuthFMExec= SensitiveFMExecs | lookup UsersAuth on SystemID, ClientID, User
| where not(EQs has FunctionModule or EQs has FunctionGroup or EQs has "*");
UnAuthFMExec
| lookup (SelectedSystems | project SystemID, SystemRole) on SystemID
| lookup UserMDSystems on SystemID, ClientID
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| extend AlertName= strcat("SAP - Unauthorized RFC Execution of a Sensitive Function Module in a ", tolower(SystemRole), " system")
| extend AlertDescription = strcat("The Function module ", FunctionModule, " of the function group ", FunctionGroup, iff(isnotempty(Description), strcat(", described as '", Description, "',"), ""), " was executed by user ", User, iff(IsUserMDThere, ". No authorization for this execution was found in the user master data profile gathered from SAP system ", ". Sentinel could not verify the authorization for this action as no user master data found for SAP system "), SystemID, ", Client ", ClientID, ". If this execution is benign, consider checking if the relevant system is configured to send the user master data profile. If you wish to allow this activity, add the specific user USER as UsersExcluded in the ‘SAP - Sensitive Function Modules’ watchlist. If you wish to allow this user to perform all sensitive FM calls (not recommended), add the user to the SAP_User_Config watchlist with a tag of 'ExecuteSensitiveFMOK'.")
| extend Dummy= ""
// | summarize by User, SystemID, ClientID, FunctionGroup, FunctionModule
| extend AlertRuleUniqueName = 'rfcexecutionofasensitivefunctionmodule'

// Define Variables
let MinutesThreshold = 15; // Difference by minutes between Create and Connect
let AuditUserCreated = dynamic(['AU7']); // Message of create user
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
// here you can exclude system users which are OK to Create Other Users
// by adding those users in the SAP_User_Config watchlist with a tag of 'CreateUserOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['CreateUserOK','SAP_ROLE', 'SAP_PROFILE']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Query Logic
let UserCreated = // Get users created
SAPAuditLog
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| where MessageID in (AuditUserCreated)
| project-rename UserCreationTimeGenerated = TimeGenerated, UserCreated = Variable1;
SAPAuditLog // Get users connections
| where MessageID in (AuditLogIn)
| lookup kind=inner UserCreated on TerminalIPv6,$left.User == $right.UserCreated, SystemID, ClientID // Lookup of user created to user log in
| where abs(datetime_diff('Minute', TimeGenerated, UserCreationTimeGenerated)) <= MinutesThreshold // Connect after creation
| summarize by TimeGenerated, Instance ,SystemID, ClientID, UserActing = User1, UserActingEmail= Email1, UserCreated = User, UserCreatedEmail= Email, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'usercreatesandusesnewuser'

// SAP - User Unlocks and uses other users
// Define variables
let MinutesThreshold = 30; // Difference by minutes between Unlock and Connect
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
// here you can exclude system users which are OK to Unlock Other Users
// by adding those users in the SAP_User_Config watchlist with a tag of 'UnlockUserOK'
// can also specify SAP Roles or Profiles to exclude
let excludeUsersTags= dynamic(['UnlockUserOK','SAP_ROLE', 'SAP_PROFILE']);
let excludedUsers= SAPUsersGetVIP(SearchForTags= excludeUsersTags)| summarize by User2Exclude=SAPUser;
// Get users which were unlocked
let UsersUnlocked = SAPAuditLog
| where MessageID == 'AUA' // Message of unlock user
| where isnotempty( TerminalIPv6)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project-rename UserUnlockTimeGenerated = TimeGenerated, UserThatWasUnlocked = Variable1, EmailWhoUnlocked = Email, UserWhoUnlocked= User
| project-keep UserUnlockTimeGenerated, UserThatWasUnlocked, EmailWhoUnlocked, SystemID, ClientID, TerminalIPv6, Instance, Host, UserWhoUnlocked;
// Query Logic
let ActionUsers = SAPAuditLog
| where isnotempty( TerminalIPv6)
| project-keep MessageID, User, Email, SystemID, TimeGenerated, ClientID, TerminalIPv6// Get users connections
| project-rename UserAction = User, EmailAction= Email
| where MessageID in (AuditLogIn);
 // check if the locked was user was unlocked and then used from the same IP address
//  as the unlocker
UsersUnlocked | join kind=inner ActionUsers on
TerminalIPv6, SystemID, ClientID, $left.UserThatWasUnlocked==$right.UserAction
| where abs(datetime_diff('Minute', TimeGenerated, UserUnlockTimeGenerated)) <= MinutesThreshold // Connect after unlock
| summarize Count=count(), TimeGenerated= max(TimeGenerated) by Instance ,SystemID, ClientID, UserWhoUnlocked , UserThatWasUnlocked, EmailWhoUnlocked, EmailThatWasUnLocked= EmailAction, TerminalIPv6, Host
| extend AlertRuleUniqueName = 'userunlocksandusesotherusers'

body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "New incident from Azure Sentinel!"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "width": "auto",
                            "style": "attention",
                            "items": [
                                {
                                    "type": "RichTextBlock",
                                    "inlines": [
                                        {
                                            "type": "TextRun",
                                            "text": "@{triggerBody()?['object']?['properties']?['severity']}"
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "spacing": "Medium",
                    "size": "Medium",
                    "weight": "Bolder",
                    "color": "Accent",
                    "text": "@{triggerBody()?['object']?['properties']?['title']}"
                },
                {
                    "type": "ActionSet",
                    "actions": [
                        {
                            "type": "Action.OpenUrl",
                            "title": "View incident",
                            "url": "@{triggerBody()?['object']?['properties']?['incidentUrl']}",
                            "id": "openSentinel"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Creation time (UTC)",
                            "value": "@{triggerBody()?['object']?['properties']?['createdTimeUtc']}"
                        },
                        {
                            "title": "Tactics",
                            "value":"@{variables('Sentinel-Tactics')}"
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "text": "@{replace(replace(triggerBody()?['object']?['properties']?['description'],'"','\"'),'\\','\')}",
                    "wrap": true
                }
            ]
        },
        {
            "type": "Container",
            "spacing": "Large",
            "style": "emphasis",
            "items": [
                {
                    "type": "TextBlock",
                    "text": "Related Entities",
                    "wrap": true,
                    "separator": true,
                    "fontType": "Default",
                    "size": "Medium",
                    "weight": "Bolder"
                },
                {
                    "type": "ColumnSet",
                    "columns": @{outputs('Compose_dynamic_json_for_entities_table')}
                }
            ],
            "bleed": true
        }
    ],
    "actions": [
        {
            "type": "Action.ShowCard",
            "title": "Block User on...",
            "style": "destructive",
            "card": {
                "type": "AdaptiveCard",
                "body": [
                    {
                        "type": "Input.ChoiceSet",
                        "id": "WhereToLockUser",
                        "label": "On which system?",
                        "style": "expanded",
                        "choices": [
                            {
                                "title": "SAP ERP",
                                "value": "erp"
                            },
                            {
                                "title": "SAP BTP Cloud Identity Service",
                                "value": "btp"
                            },
                            {
                                "title": "Azure AD (globally)",
                                "value": "aad"
                            }
                        ],
                        "value": "erp",
                        "isRequired": true,
                        "errorMessage": "Given choice unknown!"
                    },
                    {
                        "type": "Input.Text",
                        "id": "blockComment",
                        "placeholder": "Add a comment",
                        "isMultiline": true
                    }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "id": "block",
                        "title": "Submit"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.4"
            }
        },
        {
            "type": "Action.ShowCard",
            "title": "Flag as false positive",
            "card": {
                "type": "AdaptiveCard",
                "body": [
                    {
                        "type": "Input.Text",
                        "id": "flagComment",
                        "placeholder": "Add a comment",
                        "isMultiline": true
                    }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "id": "flag",
                        "title": "Submit"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.4"
            }
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "fallbackText": "This card requires Adaptive Cards v1.4 support to be rendered properly."
}

