| project-away _DTItemId
| summarize arg_max(LastUpdatedTimeUTC, *) by SearchKey
| where SearchKey == Parameter or Parameter == ""
| extend Priority= 1;
let DefaultParameters= DefaultParametersDT
| where SearchKey == Parameter or Parameter == ""
| extend Priority = 2;
let RelevantParams= (CustomersParameters | union DefaultParameters
| summarize arg_min(Priority, *) by SearchKey
| extend Source= iff(Priority==1, "'SAPSystemParameters' watchlist", "Sentinel built-in content")
| extend EnableAlerts= tolower(EnableAlerts)
| extend EnableAlerts= iff(EnableAlerts == "true", true, false), DummyJoinField= 1);
RelevantParams | join kind= inner SAPSystems() on DummyJoinField
| extend Severity= iff(SystemRole == "Production", ProductionSeverity, NonProdSeverity)
| extend ExpectedValue= iff(SystemRole == "Production", ProductionValues, NonProdValues)
| project-away DummyJoinField1, SearchKey1, ProductionSeverity, NonProdSeverity, ProductionValues, NonProdValues, Priority
| extend ExpectedNumeric= extract("([0-9.]+)", 1, replace(",",".", ExpectedValue), typeof(real))
| extend ExpectedAlpha= extract("([A-Z]+[a-z])", 1, ExpectedValue, typeof(string))

// get the relevant user MD load frequency
let UserMDTimeAgo = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
// let SnapshotAge= 0d;// allow to go back in time and see user MD snapshots
// gather the authorizations per role
let Auths= SAP_AGR_1251
| where TimeGenerated between (ago(UserMDTimeAgo+ SnapshotAge).. ago(SnapshotAge))
| summarize arg_max(TimeGenerated, OBJECT, FIELD, LOW, HIGH, AUTH) by AGR_NAME, MANDT, COUNTER, SystemID;
let AuthsEQs= Auths
| summarize Lows= make_set_if(LOW, isnotempty(LOW), 1500), Highs=make_set_if(HIGH, isnotempty(HIGH), 1500)
// , FromAuths= make_set(AUTH,1000)
by AGR_NAME, MANDT, SystemID, OBJECT, FIELD
| extend Option= 'EQ'
| extend EQs= set_union(Lows, Highs)
| project-away Lows, Highs;
let AuthsBTs= Auths | where not(isempty(HIGH))
| summarize
// FromAuths= make_set(AUTH,1000)
by AGR_NAME, MANDT, SystemID, OBJECT, FIELD, LOW, HIGH
| extend Option= 'BT';
// get the user role assignments, including child roles
let UserRoles= (SAP_AGR_USERS
| where TimeGenerated > ago (7d)
| summarize
arg_max(TimeGenerated, UNAME, AGR_NAME, MANDT, SystemID)
by UNAME, AGR_NAME, MANDT, SystemID
// get the child roles as well
| join kind= leftouter   (SAP_AGR_AGRS
| where TimeGenerated > ago(UserMDTimeAgo)
| summarize arg_max(TimeGenerated, *) by AGR_NAME, CHILD_AGR, MANDT,SystemID)
on MANDT, SystemID, AGR_NAME
// Role is the lowest level
| extend Role = iff(isempty(CHILD_AGR), AGR_NAME, CHILD_AGR)
| summarize by UNAME, MANDT, SystemID, Role);
// query
UserRoles | join kind= inner (AuthsEQs | union AuthsBTs | project-rename Role= AGR_NAME) on SystemID, MANDT, Role
|summarize Roles=make_set(Role,100), EQs=make_set(EQs, 100) by ClientID = MANDT, SystemID, User=UNAME, Object=OBJECT, Field= FIELD, Option, Low= LOW, High= HIGH

// get the relevant user MD load frequency
let UserMDTimeAgo = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
// let SnapshotAge= 0d;// allow to go back in time and see user MD snapshots
// gather the authorizations per role
let Auths= SAP_AGR_1251
| where TimeGenerated between (ago(UserMDTimeAgo+ SnapshotAge).. ago(SnapshotAge))
| summarize arg_max(TimeGenerated, OBJECT, FIELD, LOW, HIGH, AUTH) by AGR_NAME, MANDT, COUNTER, SystemID;
let AuthsEQs= Auths
| summarize Lows= make_set_if(LOW, isnotempty(LOW), 1500), Highs=make_set_if(HIGH, isnotempty(HIGH), 1500)
// , FromAuths= make_set(AUTH,1000)
by AGR_NAME, MANDT, SystemID, OBJECT, FIELD
| extend Option= 'EQ'
| extend EQs= set_union(Lows, Highs)
| project-away Lows, Highs;
let AuthsBTs= Auths | where not(isempty(HIGH))
| summarize
// FromAuths= make_set(AUTH,1000)
by AGR_NAME, MANDT, SystemID, OBJECT, FIELD, LOW, HIGH
| extend Option= 'BT';
// get the user role assignments, including child roles
let UserRoles= (SAP_AGR_USERS
| where TimeGenerated > ago (7d)
| summarize
arg_max(TimeGenerated, UNAME, AGR_NAME, MANDT, SystemID)
by UNAME, AGR_NAME, MANDT, SystemID
// get the child roles as well
| join kind= leftouter   (SAP_AGR_AGRS
| where TimeGenerated > ago(UserMDTimeAgo)
| summarize arg_max(TimeGenerated, *) by AGR_NAME, CHILD_AGR, MANDT,SystemID)
on MANDT, SystemID, AGR_NAME
// Role is the lowest level
| extend Role = iff(isempty(CHILD_AGR), AGR_NAME, CHILD_AGR)
| summarize by UNAME, MANDT, SystemID, Role);
// query
UserRoles | join kind= inner (AuthsEQs | union AuthsBTs | project-rename Role= AGR_NAME) on SystemID, MANDT, Role
|summarize Roles=make_set(Role,100), EQs=make_set(EQs, 100) by ClientID = MANDT, SystemID, User=UNAME, Object=OBJECT, Field= FIELD, Option, Low= LOW, High= HIGH

let UserMDLookBack = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
// older agents have to use USR21 to get the ADDRNUMBER, PERSNUMBER before getting the email address from ADR6
let AgentEmailsV1= SAP_USR21 | where TimeGenerated > ago(UserMDLookBack)
| summarize arg_max(TimeGenerated, ADDRNUMBER, PERSNUMBER) by BNAME, MANDT, SystemID
| project-rename CLIENT= MANDT
| join kind= leftouter hint.strategy=shuffle
(SAP_ADR6 | where TimeGenerated > ago(UserMDLookBack)
| summarize arg_max(TimeGenerated, CONSNUMBER ) by CLIENT, DATE_FROM, ADDRNUMBER, PERSNUMBER, SystemID, SMTP_ADDR)
 on ADDRNUMBER, PERSNUMBER, SystemID, CLIENT
| project SystemID, ClientID= CLIENT, User= BNAME, Email= iff(isempty(SMTP_ADDR), BNAME, SMTP_ADDR)
| summarize Email= anyif(Email, isnotempty(Email) ) by ClientID, SystemID, User;

// agents from version 60075287 have the join of USR21 and ADR6 performed
let AgentEmailsV2= SAP_ADR6 | where TimeGenerated > ago(UserMDLookBack)
| summarize arg_max(TimeGenerated, SMTP_ADDR) by BNAME, ClientID= CLIENT, SystemID
| project SystemID, ClientID, User= BNAME, Email= iff(isempty(SMTP_ADDR), BNAME, SMTP_ADDR)
| summarize Email= anyif(Email, isnotempty(Email) ) by ClientID, SystemID, User;

AgentEmailsV1 | union AgentEmailsV2

