// function gets tags which can either be SAP roles, SAP Profiles or tags.
// let SearchForTags= dynamic(['FireFighters', 'ContainsBoth', 'T-Z1220435']);
// // let SearchForTags= todynamic('All Tags');
// let SpecialFocusTags = split('SpecialAttention; SoonToLeave', ';');
// get the SAP roles / SAP profiles that are relevant
let SAPUserRolesAndProfiles = SAPUsersAssignments
| where (tostring(SearchForTags) == 'All Tags' or Profiles has_any (SearchForTags) or ChildRoles has_any (SearchForTags) or DirectRoles has_any (SearchForTags))
| project-keep User, SystemID, DirectRoles, ChildRoles, Profiles
| summarize RolesAndProfiles = make_set(array_concat(DirectRoles, ChildRoles, Profiles), 1000) by SAPUser= User
| extend SearchKey= SAPUser;
let UserMDTimeAgo = totimespan(toscalar(SAPGetParameters('UserMDLookBack')| project-keep ValueLow));
let WildCards= materialize(_GetWatchlist('SAP_User_Config')
| where SearchKey startswith '*' or SearchKey endswith '*' and SearchKey != 'SAP*'
| extend SearchKind= case(SearchKey startswith '*' and SearchKey endswith '*', 'Both', SearchKey startswith '*', 'EndsWith', 'StartsWith')
| extend SearchTerm= replace_string(SearchKey,'*','')
| extend DummyJoinField= 1);
let UsersFromWildCards= SAP_USR01 | where TimeGenerated > ago(UserMDTimeAgo)
| distinct SAPUser=BNAME
| extend DummyJoinField= 1
| join kind=inner  WildCards on DummyJoinField
| where SAPUser contains SearchTerm and SearchKind == 'Both' or
SAPUser startswith SearchTerm and SearchKind == 'StartsWith'
or SAPUser endswith SearchTerm and SearchKind == 'EndsWith'
| extend SearchKey= SAPUser;
_GetWatchlist('SAP_User_Config') | union UsersFromWildCards
| extend SearchKey= replace_string(SearchKey, ' ','')
// remove spaces
| extend Tags = replace_string(Tags, ' ', '')
| extend TagsArr= split(Tags,';')
| summarize Tags=make_set(Tags, 100), TagsList= make_set(TagsArr, 200), arg_max(LastUpdatedTimeUTC, SAPUser, ['User AAD Object Id'],	['User Identifier'],	['User On-Prem Sid'],	['User Principal Name']) by SearchKey
| join kind= fullouter SAPUserRolesAndProfiles on SAPUser
| extend SAPUser= iff(isnotempty(SAPUser), SAPUser, SAPUser1)
| extend SearchKey= SAPUser
| extend TagsList = array_concat(TagsList, RolesAndProfiles)
| extend TagsIntersect = set_intersect(TagsList, SearchForTags)
| extend SpecialFocusTagged = iff(array_length(set_intersect(TagsList, SpecialFocusTags)) > 0, true, false)
| extend IntersectionSize = array_length(TagsIntersect)
| extend Tags= replace_string(tostring(Tags),',','; ')
| extend Tags= translate('[]"','', Tags)
| where (IntersectionSize > 0 or tostring(SearchForTags) == 'All Tags' or SpecialFocusTagged == true)
| project-away LastUpdatedTimeUTC, SAPUser1, SearchKey1
| where SearchKey !startswith '*' and not(SearchKey endswith '*' and SearchKey != 'SAP*')

